å’Œå­æ­¥éª¤ -->             <div class="pixel-border p-6 panel-bg">               <div class="space-y-6 goals-container">                 <!-- ç›®æ ‡å’Œå­æ­¥éª¤å°†é€šè¿‡JavaScriptåŠ¨æ€æ¸²æŸ“ -->               </div>             </div>           </div>            <!-- å³ä¾§æ  -->           <div class="space-y-8">             <!-- è®°å½•å·¥ä½œè¿›åº¦ -->             <div class="pixel-border p-6 panel-bg">               <h3 class="font-display text-2xl text-text-primary mb-6">è®°å½•å·¥ä½œè¿›åº¦</h3>               <form id="timeline-form" class="space-y-6">                 <div>                   <label class="block font-display text-lg text-text-primary mb-2" for="work-content">å·¥ä½œå†…å®¹</label>                   <textarea id="work-content" placeholder="æè¿°æ‚¨çš„å·¥ä½œ..." rows="4"></textarea>                 </div>                 <div>                   <label class="block font-display text-lg text-text-primary mb-2" for="time-spent">æ—¶é—´ (å°æ—¶)</label>                   <input id="time-spent" placeholder="å¦‚ï¼š2" type="number" step="0.1" min="0.1" />                 </div>                 <div>                   <button type="submit"                     class="pixel-button w-full bg-accent-green text-white font-display py-3 px-4 text-sm flex items-center justify-center gap-2"                     title="è®°å½•è¿›åº¦">                     <span>ğŸ’¾</span>                     <span>è®°å½•è¿›åº¦</span>                   </button>                 </div>               </form>             </div>              <!-- è¿›åº¦å†å² -->             <div class="pixel-border p-6 panel-bg">               <h3 class="font-display text-2xl text-text-primary mb-6">è¿›åº¦å†å²</h3>               <div class="space-y-4 timeline-container">                 <!-- æ—¶é—´çº¿è®°å½•å°†é€šè¿‡JavaScriptåŠ¨æ€æ¸²æŸ“ -->               </div>             </div>           </div>          </div>       </div>     </main>   </div>    <script>     // ç¡®ä¿ TaskPixel åç§°ç©ºé—´å­˜åœ¨ï¼Œå³ä½¿ core.js æœªèƒ½æ­£ç¡®åŠ è½½     window.TaskPixel = window.TaskPixel || {};      // ç»Ÿä¸€çš„ç¼–è¾‘ä»»åŠ¡å‡½æ•°     function editTask(taskId) {       if (!taskId) {         alert('æœªæ‰¾åˆ°ä»»åŠ¡ID');         return;       }        let data, task;        try {         const raw = localStorage.getItem('taskpixel_data');         if (!raw) {           alert('æœªæ‰¾åˆ°ä»»åŠ¡æ•°æ®');           return;         }          data = JSON.parse(raw);         task = data.tasks.find(t => t.id === taskId);          if (!task) {           alert('æœªæ‰¾åˆ°æŒ‡å®šä»»åŠ¡');           return;         }        } catch (parseError) {         console.error('è¯»å–ä»»åŠ¡æ•°æ®é”™è¯¯:', parseError);         alert('è¯»å–ä»»åŠ¡æ•°æ®å¤±è´¥: ' + parseError.message);         return;       }        try {         // åˆ›å»ºç¼–è¾‘å¯¹è¯æ¡†         const dialogElement = document.createElement('div');         dialogElement.className = 'fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50';         dialogElement.id = 'edit-task-dialog';          // è®¡ç®—å½“å‰è¿›åº¦ï¼Œå†³å®šçŠ¶æ€é€‰é¡¹         let currentProgress = window.TaskPixel?.DataStoreAdapter?.getTaskProgress(taskId) || task.progress || 0;         let currentStatus = task.status || 'in-progress';         let isCompleted = currentProgress >= 100;          dialogElement.innerHTML = `           <div class="pixel-border bg-white p-6 w-full max-w-lg">             <h2 class="text-2xl font-display mb-6">ç¼–è¾‘ä»»åŠ¡</h2>             <form id="edit-task-form">               <div class="mb-4">                 <label class="block font-display text-lg mb-2" for="edit-task-title">ä»»åŠ¡æ ‡é¢˜</label>                 <input type="text" id="edit-task-title" class="w-full" required placeholder="è¾“å…¥ä»»åŠ¡æ ‡é¢˜" value="${task.title || ''}">               </div>               <div class="mb-4">                 <label class="block font-display text-lg mb-2" for="edit-task-description">ä»»åŠ¡æè¿°</label>                 <textarea id="edit-task-description" class="w-full h-32" placeholder="è¾“å…¥ä»»åŠ¡æè¿°">${task.description || ''}</textarea>               </div>               ${isCompleted ? `               <div class="mb-4">                 <label class="block font-display text-lg mb-2">çŠ¶æ€</label>                 <div class="w-full p-2 bg-green-100 text-green-800 border border-green-300 rounded">                   âœ… å·²å®Œæˆï¼ˆè¿›åº¦100%ï¼Œè‡ªåŠ¨è®¾ç½®ï¼‰                 </div>               </div>               ` : `               <div class="mb-4">                 <label class="block font-display text-lg mb-2" for="edit-task-status">çŠ¶æ€</label>                 <select id="edit-task-status" class="w-full">                   <option value="in-progress" ${currentStatus === 'in-progress' || currentStatus === 'todo' ? 'selected' : ''}>è¿›è¡Œä¸­</option>                   <option value="on-hold" ${currentStatus === 'on-hold' ? 'selected' : ''}>æç½®</option>                 </select>               </div>               `}               <div class="mb-4">                 <label class="block font-display text-lg mb-2">å½“å‰è¿›åº¦: ${currentProgress}%</label>                 <div class="w-full bg-gray-200 rounded-full h-2">                   <div class="bg-blue-600 h-2 rounded-full" style="width: ${currentProgress}%"></div>                 </div>               </div>               <div class="flex justify-end gap-4">                 <button type="button" id="cancel-edit-task" class="pixel-button">å–æ¶ˆ</button>                 <button type="submit" class="pixel-button bg-primary text-white">ä¿å­˜</button>               </div>             </form>           </div>         `;          document.body.appendChild(dialogElement);          // ç»‘å®šè¡¨å•æäº¤äº‹ä»¶         document.getElementById('edit-task-form').addEventListener('submit', function (e) {           e.preventDefault();            try {             const title = document.getElementById('edit-task-title').value.trim();             const description = document.getElementById('edit-task-description').value.trim();              if (!title) {               alert('ä»»åŠ¡æ ‡é¢˜ä¸èƒ½ä¸ºç©º');               return;             }              // ç¡®å®šæœ€ç»ˆçŠ¶æ€             let finalStatus;             const progress = window.TaskPixel?.DataStoreAdapter?.getTaskProgress(taskId) || task.progress || 0;              if (progress >= 100) {               finalStatus = 'completed';             } else {               const statusSelect = document.getElementById('edit-task-status');               finalStatus = statusSelect ? statusSelect.value : 'in-progress';             }              // æ›´æ–°ä»»åŠ¡æ•°æ®             task.title = title;             task.description = description;             task.status = finalStatus;              // ä¿å­˜åˆ°localStorage             localStorage.setItem('taskpixel_data', JSON.stringify(data));              // æ˜¾ç¤ºæˆåŠŸæ¶ˆæ¯             alert('ä»»åŠ¡æ›´æ–°æˆåŠŸï¼');              // å…³é—­å¯¹è¯æ¡†             const dialogElement = document.getElementById('edit-task-dialog');             if (dialogElement) {               dialogElement.remove();             }              // åˆ·æ–°é¡µé¢             location.reload();            } catch (submitError) {             console.error('è¡¨å•æäº¤é”™è¯¯:', submitError);             alert('ä¿å­˜ä»»åŠ¡æ—¶å‡ºç°é”™è¯¯ï¼Œè¯·é‡è¯•ï¼š' + submitError.message);           }         });          // ç»‘å®šå–æ¶ˆæŒ‰é’®         document.getElementById('cancel-edit-task').addEventListener('click', function () {           const dialogElement = document.getElementById('edit-task-dialog');           if (dialogElement) {             dialogElement.remove();           }         });        } catch (dialogError) {         console.error('åˆ›å»ºç¼–è¾‘å¯¹è¯æ¡†é”™è¯¯:', dialogError);         alert('åˆ›å»ºç¼–è¾‘å¯¹è¯æ¡†å¤±è´¥: ' + dialogError.message);       }     }      // ç»Ÿä¸€çš„å®Œæˆä»»åŠ¡å‡½æ•°     function completeTask(taskId) {       if (!taskId) {         alert('æœªæ‰¾åˆ°ä»»åŠ¡ID');         return;       }        try {         const raw = localStorage.getItem('taskpixel_data');         if (!raw) {           alert('æœªæ‰¾åˆ°ä»»åŠ¡æ•°æ®');           return;         }          const data = JSON.parse(raw);         const task = data.tasks.find(t => t.id === taskId);          if (!task) {           alert('æœªæ‰¾åˆ°æŒ‡å®šä»»åŠ¡');           return;         }          // åˆ‡æ¢å®ŒæˆçŠ¶æ€         const oldStatus = task.status;         task.status = task.status === 'completed' ? 'in-progress' : 'completed';          // å¦‚æœæ ‡è®°ä¸ºå®Œæˆï¼Œè®¾ç½®è¿›åº¦ä¸º100%         if (task.status === 'completed') {           task.progress = 100;         }          // ä¿å­˜åˆ°localStorage         localStorage.setItem('taskpixel_data', JSON.stringify(data));          // æ˜¾ç¤ºæˆåŠŸæ¶ˆæ¯         const statusText = task.status === 'completed' ? 'å·²å®Œæˆ' : 'è¿›è¡Œä¸­';         alert(`ä»»åŠ¡çŠ¶æ€å·²æ›´æ–°ä¸ºï¼š${statusText}`);          // åˆ·æ–°é¡µé¢         location.reload();        } catch (e) {         console.error('æ›´æ–°ä»»åŠ¡çŠ¶æ€é”™è¯¯:', e);         alert('æ›´æ–°ä»»åŠ¡çŠ¶æ€æ—¶å‡ºé”™: ' + e.message);       }     }      // æ·»åŠ å·¥ä½œè¿›åº¦è®°å½•     function addWorkProgress(taskId, content, hours) {       if (!taskId) {         alert('æœªæ‰¾åˆ°ä»»åŠ¡ID');         return;       }        try {         const raw = localStorage.getItem('taskpixel_data');         if (!raw) {           alert('æœªæ‰¾åˆ°ä»»åŠ¡æ•°æ®');           return;         }          const data = JSON.parse(raw);         const task = data.tasks.find(t => t.id === taskId);          if (!task) {           alert('æœªæ‰¾åˆ°æŒ‡å®šä»»åŠ¡');           return;         }          // åˆå§‹åŒ–timelineæ•°ç»„         if (!task.timeline) {           task.timeline = [];         }          // åˆ›å»ºæ–°çš„è¿›åº¦è®°å½•         const progressEntry = {           id: 'progress-' + Date.now(),           date: new Date().toISOString(),           content: content,           hours: hours,           timestamp: Date.now()         };          task.timeline.push(progressEntry);          // æ›´æ–°æ€»å·¥ä½œæ—¶é—´         const totalHours = task.timeline.reduce((sum, entry) => sum + (entry.hours || 0), 0);         task.totalHours = totalHours;          // ä¿å­˜åˆ°localStorage         localStorage.setItem('taskpixel_data', JSON.stringify(data));          // æ¸…ç©ºè¡¨å•         document.getElementById('work-content').value = '';         document.getElementById('time-spent').value = '';          // é‡æ–°æ¸²æŸ“è¿›åº¦å†å²         renderProgressHistory(task.timeline);          alert('å·¥ä½œè¿›åº¦è®°å½•æˆåŠŸï¼');        } catch (e) {         alert('è®°å½•å·¥ä½œè¿›åº¦æ—¶å‡ºé”™: ' + e.message);       }     }      // æ¸²æŸ“è¿›åº¦å†å²     function renderProgressHistory(timeline) {       const container = document.querySelector('.timeline-container');       if (!container) return;        container.innerHTML = '';        if (!timeline || timeline.length === 0) {         container.innerHTML = `           <div class="text-center py-8">             <p class="text-gray-500 mb-4">è¿˜æ²¡æœ‰å·¥ä½œè¿›åº¦è®°å½•</p>             <p class="text-gray-400 text-sm">å¼€å§‹è®°å½•æ‚¨çš„å·¥ä½œè¿›åº¦å§ï¼</p>           </div>         `;         return;       }        // æŒ‰æ—¶é—´å€’åºæ’åˆ—       const sortedTimeline = [...timeline].sort((a, b) => b.timestamp - a.timestamp);        sortedTimeline.forEach(entry => {         const entryElement = document.createElement('div');         entryElement.className = 'pixel-border bg-white p-4 mb-4 hover:shadow-lg transition-shadow';          const date = new Date(entry.date);         const formattedDate = date.toLocaleDateString('zh-CN') + ' ' + date.toLocaleTimeString('zh-CN', { hour: '2-digit', minute: '2-digit' });          entryElement.innerHTML = `           <div class="flex justify-between items-start mb-3">             <div class="flex items-center gap-2">               <div class="w-3 h-3 bg-primary"></div>               <span class="font-display text-sm text-gray-600">${formattedDate}</span>             </div>             <span class="pixel-button bg-primary text-white px-3 py-1 text-xs">${entry.hours} å°æ—¶</span>           </div>           <div class="pl-5">             <p class="font-display text-lg text-text-primary">${entry.content}</p>           </div>         `;          container.appendChild(entryElement);       });     }   </script>   <script src="js/core.js"></script>   <script src="js/dataStoreAdapter.js"></script>   <script src="js/tagManager.js"></script>   <script src="js/aiAssist.js"></script>   <script src="js/taskDetail.js"></script>   <script>     // ç®€åŒ–çš„åˆå§‹åŒ–è„šæœ¬ - ç§»é™¤æ‰€æœ‰è°ƒè¯•è¾“å‡º     document.addEventListener('DOMContentLoaded', function () {       // åŸºç¡€æŒ‰é’®äº‹ä»¶       document.querySelector('.back-button')?.addEventListener('click', function () {         window.location.href = 'index.html';       });        document.querySelector('.edit-button')?.addEventListener('click', function () {         const taskId = new URLSearchParams(window.location.search).get('id') || window.currentTaskId;         editTask(taskId);       });        document.querySelector('.complete-button')?.addEventListener('click', function () {         const taskId = new URLSearchParams(window.location.search).get('id');         completeTask(taskId);       });        // è®°å½•å·¥ä½œè¿›åº¦è¡¨å•å¤„ç†       document.getElementById('timeline-form')?.addEventListener('submit', function (e) {         e.preventDefault();         const taskId = new URLSearchParams(window.location.search).get('id');         const workContent = document.getElementById('work-content').value.trim();         const timeSpent = parseFloat(document.getElementById('time-spent').value);          if (!workContent) {           alert('è¯·è¾“å…¥å·¥ä½œå†…å®¹');           return;         }         if (!timeSpent || timeSpent <= 0) {           alert('è¯·è¾“å…¥æœ‰æ•ˆçš„å·¥ä½œæ—¶é—´');           return;         }          addWorkProgress(taskId, workContent, timeSpent);       });        // åˆå§‹åŒ– TaskPixel       try {         if (typeof TaskPixel?.init === 'function') {           TaskPixel.init();         }       } catch (e) {         // é™é»˜å¤„ç†é”™è¯¯       }        // åˆå§‹åŒ– TaskDetail       try {         const taskId = new URLSearchParams(window.location.search).get('id');         if (taskId && window.TaskPixel?.TaskDetail?.init) {           window.currentTaskId = taskId;           TaskPixel.TaskDetail.currentTaskId = taskId;           TaskPixel.TaskDetail.init();         }       } catch (e) {         // é™é»˜å¤„ç†é”™è¯¯       }        // å†…è”æ ‡ç­¾ç®¡ç†å™¨å°†åœ¨å®šä¹‰ååˆå§‹åŒ–        // å¤‡ç”¨æ•°æ®æ˜¾ç¤ºé€»è¾‘ - ç¡®ä¿ä»»åŠ¡ä¿¡æ¯èƒ½å¤Ÿæ˜¾ç¤º       setTimeout(function () {         const taskId = new URLSearchParams(window.location.search).get('id');         if (taskId) {           try {             const raw = localStorage.getItem('taskpixel_data');             if (raw) {               const data = JSON.parse(raw);               const task = (data.tasks || []).find(t => t.id === taskId);                if (task) {                 // æ›´æ–°æ ‡é¢˜                 const titleEl = document.querySelector('.task-title');                 if (titleEl && !titleEl.textContent.includes(task.title)) {                   titleEl.textContent = 'ä»»åŠ¡: ' + task.title;                 }                  // è®¾ç½®ä»»åŠ¡æ ‡ç­¾å®¹å™¨çš„ task-id                 const taskTagContainer = document.querySelector('[data-target="task"]');                 if (taskTagContainer) {                   taskTagContainer.dataset.taskId = taskId;                 }                  // æ¸²æŸ“ä»»åŠ¡æ ‡ç­¾ï¼ˆä»…åœ¨å®¹å™¨ä¸ºç©ºæ—¶ï¼‰                 const taskTagsDisplay = document.querySelector('.task-tags-display');                 if (typeof InlineTagManager !== 'undefined' && InlineTagManager.renderTaskTags &&                   taskTagsDisplay && taskTagsDisplay.children.length === 0) {                   InlineTagManager.renderTaskTags(taskId);                 }                  // æ›´æ–°æè¿°                 const descEl = document.querySelector('.task-description');                 if (descEl && !descEl.textContent) {                   descEl.textContent = task.description || 'æš‚æ— æè¿°';                 }                  // æ›´æ–°è¿›åº¦ - ä½¿ç”¨ç»Ÿä¸€çš„ updateProgressBar å‡½æ•°                 updateProgressBar(taskId);                  // æ›´æ–°çŠ¶æ€                 const statusEl = document.querySelector('.task-status');                 if (statusEl) {                   let statusText = 'è¿›è¡Œä¸­';                   let statusClass = 'bg-accent-yellow/20 text-accent-yellow-800 border-accent-yellow-800';                    switch (task.status) {                     case 'todo':                       statusText = 'å¾…å¤„ç†';                       statusClass = 'bg-accent-blue/20 text-accent-blue-800 border-accent-blue-800';                       break;                     case 'on-hold':                       statusText = 'æç½®';                       statusClass = 'bg-accent-red/20 text-accent-red-800 border-accent-red-800';                       break;                     case 'completed':                       statusText = 'å·²å®Œæˆ';                       statusClass = 'bg-accent-green/20 text-accent-green-800 border-accent-green-800';                       break;                     case 'in-progress':                     default:                       statusText = 'è¿›è¡Œä¸­';                       statusClass = 'bg-accent-yellow/20 text-accent-yellow-800 border-accent-yellow-800';                       break;                   }                   statusEl.textContent = statusText;                    // æ›´æ–°çŠ¶æ€æ ·å¼                   statusEl.className = `font-display text-sm px-2 py-1 task-status border-2 ${statusClass}`;                 }                  // æ¸²æŸ“ç›®æ ‡                 const goalsContainer = document.querySelector('.goals-container');                 if (goalsContainer) {                   renderGoals(task.goals || [], taskId);                   // æ¸²æŸ“ç›®æ ‡åæ›´æ–°è¿›åº¦æ¡                   updateProgressBar(taskId);                 }                  // æ¸²æŸ“è¿›åº¦å†å²                 if (task.timeline) {                   renderProgressHistory(task.timeline);                 }               }             }           } catch (e) {             // é™é»˜å¤„ç†é”™è¯¯           }         }       }, 1000);        // ç›®æ ‡æ¸²æŸ“å‡½æ•°       function renderGoals(goals, taskId) {         const goalsContainer = document.querySelector('.goals-container');         if (!goalsContainer) return;          // æ¸…ç©ºå®¹å™¨         goalsContainer.innerHTML = '';          // æ·»åŠ æ ‡é¢˜å’ŒAIåŠ©æ‰‹æŒ‰é’®         const header = document.createElement('div');         header.className = 'flex items-center justify-between mb-6';         header.innerHTML = `           <h3 class="font-display text-2xl text-text-primary">ç›®æ ‡ä¸å­æ­¥éª¤</h3>           <button class="pixel-button bg-purple-600 text-white px-4 py-2 text-sm">             <span class="mr-2">âœ¨</span>AI åŠ©æ‰‹           </button>         `;         goalsContainer.appendChild(header);          // æ¸²æŸ“ç›®æ ‡åˆ—è¡¨         if (goals && goals.length > 0) {           goals.forEach(goal => {             const goalCard = createGoalCard(goal, taskId);             goalsContainer.appendChild(goalCard);              // æ¸²æŸ“ç›®æ ‡æ ‡ç­¾ï¼ˆä»…åœ¨å®¹å™¨ä¸ºç©ºæ—¶ï¼‰             const goalTagsDisplay = goalCard.querySelector('.goal-tags-display');             if (typeof InlineTagManager !== 'undefined' && InlineTagManager.renderGoalTags &&               goalTagsDisplay && goalTagsDisplay.children.length === 0) {               InlineTagManager.renderGoalTags(goal.id);             }           });            // æ·»åŠ "æ·»åŠ æ–°ç›®æ ‡"æŒ‰é’®           const addButton = document.createElement('div');           addButton.className = 'text-center mt-6';           addButton.innerHTML = `             <button class="pixel-