  } catch (e) {           console.error('åº”ç”¨æŠ˜å é¦–é€‰é¡¹/åˆå§‹åŒ–å­æ­¥éª¤ sortable æ—¶å‡ºé”™', e);         }       }        // åˆå§‹åŒ–å­æ­¥éª¤æ‹–åŠ¨æ’åº       function initializeSubstepSorting(goalCard, goalId, taskId) {         const substepsContainer = goalCard.querySelector('.substeps-container');         if (!substepsContainer || !window.Sortable) return;          // åˆ›å»ºSortableå®ä¾‹         new Sortable(substepsContainer, {           handle: '.drag-handle',           animation: 150,           ghostClass: 'sortable-ghost',           chosenClass: 'sortable-chosen',           dragClass: 'sortable-drag',           onEnd: function (evt) {             // è·å–æ–°çš„æ’åº             const substepItems = Array.from(substepsContainer.querySelectorAll('.substep-item'));             const newOrder = substepItems.map(item => item.dataset.substepId);              // æ›´æ–°æ•°æ®å­˜å‚¨             updateSubstepOrder(taskId, goalId, newOrder);           }         });       }        // æ›´æ–°å­æ­¥éª¤æ’åº       function updateSubstepOrder(taskId, goalId, newOrder) {         try {           // ä¼˜å…ˆä½¿ç”¨ DataStore API           if (window.TaskPixel && TaskPixel.DataStore && typeof TaskPixel.DataStore.updateSubstepOrder === 'function') {             TaskPixel.DataStore.updateSubstepOrder(taskId, goalId, newOrder);             return;           }         } catch (e) {           console.error('æ›´æ–°å­æ­¥éª¤æ’åºå¤±è´¥ via DataStore', e);         }          // å¤‡ç”¨æ–¹æ¡ˆï¼šç›´æ¥æ“ä½œlocalStorage         try {           const raw = localStorage.getItem('taskpixel_data');           if (!raw) return;            const data = JSON.parse(raw);           const task = data.tasks.find(t => t.id === taskId);           if (!task || !task.goals) return;            const goal = task.goals.find(g => g.id === goalId);           if (!goal || !goal.substeps) return;            // é‡æ–°æ’åºå­æ­¥éª¤           const reorderedSubsteps = [];           newOrder.forEach((substepId, index) => {  