tBtn) {           editBtn.addEventListener('click', () => openEditGoalDialog(goal.id, taskId));         }          // åˆ é™¤ç›®æ ‡         const deleteBtn = card.querySelector('.delete-goal-btn');         if (deleteBtn) {           deleteBtn.addEventListener('click', () => {             if (confirm('ç¡®å®šè¦åˆ é™¤è¿™ä¸ªç›®æ ‡å—ï¼Ÿ')) {               deleteGoal(taskId, goal.id);             }           });         }          // å¤é€‰æ¡†äº‹ä»¶         const checkboxes = card.querySelectorAll('.substep-checkbox');         checkboxes.forEach(checkbox => {           checkbox.addEventListener('change', function () {             updateSubstepStatus(               taskId,               this.dataset.goalId,               this.dataset.substepId,               this.checked             );           });         });          // ä¿®æ”¹å­æ­¥éª¤æŒ‰é’®         const editSubstepButtons = card.querySelectorAll('.edit-substep-btn');         editSubstepButtons.forEach(btn => {           btn.addEventListener('click', () => {             openEditSubstepDialog(taskId, btn.dataset.goalId, btn.dataset.substepId);           });         });          // åˆ é™¤å­æ­¥éª¤æŒ‰é’®         const deleteButtons = card.querySelectorAll('.delete-substep-btn');         deleteButtons.forEach(btn => {           btn.addEventListener('click', () => {             if (confirm('ç¡®å®šè¦åˆ é™¤è¿™ä¸ªå­æ­¥éª¤å—ï¼Ÿ')) {               deleteSubstep(taskId, btn.dataset.goalId, btn.dataset.substepId);             }           });         });            // åˆå§‹åŒ–å­æ­¥éª¤æ‹–åŠ¨æ’åº         initializeSubstepSorting(card, goal.id, taskId);          // æŠ˜å /å±•å¼€å­æ­¥éª¤åŠŸèƒ½         try {           const toggleBtn = card.querySelector('.toggle-substeps-btn');           const wrapper = card.querySelector('.substeps-wrapper');           const container = wrapper && wrapper.querySelector('.substeps-container');            // helper: è¯»å–/å†™å…¥ UI prefs           function getUIPrefs() {             try {               const raw = localStorage.getItem('taskpixel_ui');               return raw ? JSON.parse(raw) : {};             } catch (e) { return {}; }           }           function setUIPrefs(prefs) {             try { localStorage.setItem('taskpixel_ui', JSON.stringify(prefs)); } catch (e) { }           }            if (toggleBtn && container) {             // åˆå§‹åŒ–æŠ˜å çŠ¶æ€ï¼ˆä¼˜å…ˆä» taskpixel_ui ä¸­è¯»å–ï¼‰             const prefs = getUIPrefs();             prefs.collapsedGoals = prefs.collapsedGoals || {};             prefs.collapsedGoals[taskId] = prefs.collapsedGoals[taskId] || {};             const collapsed = !!prefs.collapsedGoals[taskId][goal.id];             container.setAttribute('data-collapsed', collapsed ? 'true' : 'false');             if (collapsed) container.style.display = 'none';             // è®¾ç½®åˆå§‹çŠ¶æ€             toggleBtn.setAttribute('aria-expanded', collapsed ? 'false' : 'true');              toggleBtn.addEventListener('click', () => {               const isCollapsed = container.getAttribute('data-collapsed') === 'true';               if (isCollapsed) {                 container.setAttribute('data-collapsed', 'false');                 container.style.display = '';                 prefs.collapsedGoals[taskId][goal.id] = false;               } else {                 container.setAttribute('data-collapsed', 'true');                 container.style.display = 'none';                 prefs.collapsedGoals[taskId][goal.id] = true;               }               // æ›´æ–° aria-expanded å±æ€§ï¼ŒCSS ä¼šè‡ªåŠ¨æ›´æ–°ç®­å¤´æ–¹å‘               toggleBtn.setAttribute('aria-expanded', isCollapsed ? 'true' : 'false');               setUIPrefs(prefs);             });           }         } catch (e) {           console.error('åˆå§‹åŒ–æŠ˜å å­æ­¥éª¤å¤±è´¥', e);         }       }            // ç»‘å®šæ·»åŠ ç›®æ ‡äº‹ä»¶       function bindAddGoalEvents(taskId) {         const addBtns = document.querySelectorAll('.add-goal-btn');         addBtns.forEach(btn => {           btn.addEventListener('click', () => openAddGoalDialog(taskId));         });       }        // æ‰“å¼€æ·»åŠ ç›®æ ‡å¯¹è¯æ¡†       function openAddGoalDialog(taskId) {         const dialog = document.createElement('div');         dialog.className = 'fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50';         dialog.id = 'add-goal-dialog';          dialog.innerHTML = `           <div class="pixel-border bg-white p-6 w-full max-w-lg">             <h2 class="text-2xl font-display mb-6">æ·»åŠ ç›®æ ‡</h2>             <form id="add-goal-form">               <div class="mb-4">                 <label class="block font-display text-lg mb-2" for="goal-title">ç›®æ ‡æ ‡é¢˜ *</label>                 <input type="text" id="goal-title" class="w-full" required placeholder="è¾“å…¥ç›®æ ‡æ ‡é¢˜">               </div>               <div class="mb-4">                 <label class="block font-display text-lg mb-2" for="goal-description">ç›®æ ‡æè¿°</label>                 <textarea id="goal-description" class="w-full h-32" placeholder="è¾“å…¥ç›®æ ‡æè¿°ï¼ˆå¯é€‰ï¼‰"></textarea>               </div>               <div class="flex justify-end gap-4">                 <button type="button" id="cancel-add-goal" class="pixel-button">å–æ¶ˆ</button>                 <button type="submit" class="pixel-button bg-primary text-white">æ·»åŠ </button>               </div>             </form>           </div>         `;          document.body.appendChild(dialog);          // ç»‘å®šäº‹ä»¶         document.getElementById('add-goal-form').addEventListener('submit', function (e) {           e.preventDefault();           const title = document.getElementById('goal-title').value.trim();           const description = document.getElementById('goal-description').value.trim();            if (!title) {             alert('è¯·è¾“å…¥ç›®æ ‡æ ‡é¢˜');             return;           }            addGoal(taskId, title, description);           dialog.remove();         });          document.getElementById('cancel-add-goal').addEventListener('click', () => {           dialog.remove();         });          // èšç„¦åˆ°æ ‡é¢˜è¾“å…¥æ¡†         setTimeout(() => {           document.getElementById('goal-title').focus();         }, 100);       }        // æ·»åŠ ç›®æ ‡       function addGoal(taskId, title, description) {         try {           const raw = localStorage.getItem('taskpixel_data');           if (raw) {             const data = JSON.parse(raw);             const task = data.tasks.find(t => t.id === taskId);             if (task) {               if (!task.goals) task.goals = [];                const newGoal = {                 id: 'goal-' + Date.now(),                 title: title,                 description: description || '',                 substeps: []               };                task.goals.push(newGoal);               localStorage.setItem('taskpixel_data', JSON.stringify(data));                // é‡æ–°æ¸²æŸ“               renderGoals(task.goals, taskId);             }           }         } catch (e) {           alert('æ·»åŠ ç›®æ ‡å¤±è´¥: ' + e.message);         }       }        // æ‰“å¼€ä¿®æ”¹ç›®æ ‡å¯¹è¯æ¡†       function openEditGoalDialog(goalId, taskId) {         try {           const raw = localStorage.getItem('taskpixel_data');           if (!raw) return;            const data = JSON.parse(raw);           const task = data.tasks.find(t => t.id === taskId);           if (!task || !task.goals) return;            const goal = task.goals.find(g => g.id === goalId);           if (!goal) return;            const dialog = document.createElement('div');           dialog.className = 'fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50';           dialog.id = 'edit-goal-dialog';            dialog.innerHTML = `             <div class="pixel-border bg-white p-6 w-full max-w-lg">               <h2 class="text-2xl font-display mb-6">ä¿®æ”¹ç›®æ ‡</h2>               <form id="edit-goal-form">                 <div class="mb-4">               