         alert('ä¿®æ”¹ç›®æ ‡å¤±è´¥: ' + e.message);         }       }        // åˆ é™¤ç›®æ ‡       function deleteGoal(taskId, goalId) {         try {           const raw = localStorage.getItem('taskpixel_data');           if (raw) {             const data = JSON.parse(raw);             const task = data.tasks.find(t => t.id === taskId);             if (task && task.goals) {               task.goals = task.goals.filter(g => g.id !== goalId);               localStorage.setItem('taskpixel_data', JSON.stringify(data));                // é‡æ–°æ¸²æŸ“               renderGoals(task.goals, taskId);               updateProgressBar(taskId);             }           }         } catch (e) {           alert('åˆ é™¤ç›®æ ‡å¤±è´¥: ' + e.message);         }       }        // æ‰“å¼€æ·»åŠ å­æ­¥éª¤å¯¹è¯æ¡†       function openAddSubstepDialog(goalId, taskId) {         const dialog = document.createElement('div');         dialog.className = 'fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50';         dialog.id = 'add-substep-dialog';          dialog.innerHTML = `           <div class="pixel-border bg-white p-6 w-full max-w-lg">             <h2 class="text-2xl font-display mb-6">æ·»åŠ å­æ­¥éª¤</h2>             <form id="add-substep-form">               <div class="mb-4">                 <label class="block font-display text-lg mb-2" for="substep-content">å­æ­¥éª¤å†…å®¹ *</label>                 <input type="text" id="substep-content" class="w-full" required placeholder="è¾“å…¥å­æ­¥éª¤å†…å®¹">               </div>               <div class="flex justify-end gap-4">                 <button type="button" id="cancel-add-substep" class="pixel-button">å–æ¶ˆ</button>                 <button type="submit" class="pixel-button bg-primary text-white">æ·»åŠ </button>               </div>             </form>           </div>         `;          document.body.appendChild(dialog);          // ç»‘å®šäº‹ä»¶         document.getElementById('add-substep-form').addEventListener('submit', function (e) {           e.preventDefault();           const content = document.getElementById('substep-content').value.trim();            if (!content) {             alert('è¯·è¾“å…¥å­æ­¥éª¤å†…å®¹');             return;           }            addSubstep(goalId, taskId, content);           dialog.remove();         });          document.getElementById('cancel-add-substep').addEventListener('click', () => {           dialog.remove();         });          // èšç„¦åˆ°å†…å®¹è¾“å…¥æ¡†         setTimeout(() => {           document.getElementById('substep-content').focus();         }, 100);       }        // æ·»åŠ å­æ­¥éª¤       function addSubstep(goalId, taskId, content) {         try {           const raw = localStorage.getItem('taskpixel_data');           if (raw) {             const data = JSON.parse(raw);             const task = data.tasks.find(t => t.id === taskId);             if (task && task.goals) {               const goal = task.goals.find(g => g.id === goalId);               if (goal) {                 if (!goal.substeps) goal.substeps = [];                  const newSubstep = {                   id: 'substep-' + Date.now(),                   content: content,                   completed: false                 };                  goal.substeps.push(newSubstep);                 localStorage.setItem('taskpixel_data', JSON.stringify(data));                  // é‡æ–°æ¸²æŸ“                 renderGoals(task.goals, taskId);                 updateProgressBar(taskId);               }             }           }         } catch (e) {           alert('æ·»åŠ å­æ­¥éª¤å¤±è´¥: ' + e.message);         }       }        // æ‰“å¼€ä¿®æ”¹å­æ­¥éª¤å¯¹è¯æ¡†       function openEditSubstepDialog(taskId, goalId, substepId) {         try {           const raw = localStorage.getItem('taskpixel_data');           if (!raw) return;            const data = JSON.parse(raw);           const task = data.tasks.find(t => t.id === taskId);           if (!task || !task.goals) return;            const goal = task.goals.find(g => g.id === goalId);           if (!goal || !goal.substeps) return;            const substep = goal.substeps.find(s => s.id === substepId);           if (!substep) return;            const dialog = document.createElement('div');           dialog.className = 'fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50';           dialog.id = 'edit-substep-dialog';            dialog.innerHTML = `             <div class="pixel-border bg-white p-6 w-full max-w-lg">               <h2 class="text-2xl font-display mb-6">ä¿®æ”¹å­æ­¥éª¤</h2>               <form id="edit-substep-form">                 <div class="mb-4">                   <label class="block font-display text-lg mb-2" for="edit-substep-content">å­æ­¥éª¤å†…å®¹ *</label>                   <input type="text" id="edit-substep-content" class="w-full" required placeholder="è¾“å…¥å­æ­¥éª¤å†…å®¹" value="${substep.content || substep.title || ''}">                 </div>                 <div class="mb-4">                   <label class="flex items-center">                     <input type="checkbox" id="edit-substep-completed" ${substep.completed ? 'checked' : ''} class="mr-2">                     <span class="font-display text-lg">å·²å®Œæˆ</span>                   </label>                 </div>                 <div class="flex justify-end gap-4">                   <button type="button" id="cancel-edit-substep" class="pixel-button">å–æ¶ˆ</button>                   <button type="submit" class="pixel-button bg-primary text-white">ä¿å­˜</button>                 </div>               </form>             </div>           `;            document.body.appendChild(dialog);            // ç»‘å®šäº‹ä»¶           document.getElementById('edit-substep-form').addEventListener('submit', function (e) {             e.preventDefault();             const content = document.getElementById('edit-substep-content').value.trim();             const completed = document.getElementById('edit-substep-completed').checked;              if (!content) {               alert('è¯·è¾“å…¥å­æ­¥éª¤å†…å®¹');               return;             }              // æ›´æ–°å­æ­¥éª¤             substep.content = content;             substep.completed = completed;             localStorage.setItem('taskpixel_data', JSON.stringify(data));              // é‡æ–°æ¸²æŸ“             renderGoals(task.goals, taskId);             updateProgressBar(taskId);             dialog.remove();           });            document.getElementById('cancel-edit-substep').addEventListener('click', () => {             dialog.remove();           });            // èšç„¦åˆ°å†…å®¹è¾“å…¥æ¡†           setTimeout(() => {             document.getElementById('edit-substep-content').focus();           }, 100);          } catch (e) {           alert('ä¿®æ”¹å­æ­¥éª¤å¤±è´¥: ' + e.message);         }       }        // åˆ é™¤å­æ­¥éª¤       function deleteSubstep(taskId, goalId, substepId) {         try {           const raw = localStorage.getItem('taskpixel_data');           if (raw) {             const data = JSON.parse(raw);             const task = data.tasks.find(t => t.id === taskId);             if (task && task.goals) {               const goal = task.goals.find(g => g.id === goalId);               if (goal && goal.substeps) {                 goal.substeps = goal.substeps.filter(s => s.id !== substepId);                 localStorage.setItem('taskpixel_data', JSON.stringify(data));                  // é‡æ–°æ¸²æŸ“                 renderGoals(task.goals, taskId);                 updateProgressBar(taskId);               }             }           }         } catch (e) {           alert('åˆ é™¤å­æ­¥éª¤å¤±è´¥: ' + e.message);         }       }        // æ›´æ–°å­æ­¥éª¤çŠ¶æ€       function updateSubstepStatus(taskId, goalId, substepId, completed) {         try {           const raw = localStorage.getItem('taskpixel_data');           if (raw) {             const data = JSON.parse(raw);             const task = data.tasks.find(t => t.id === taskId);             if (task && task.goals) {               const goal = task.goals.find(g => g.id === goalId);               if (goal && goal.substeps) {                 const substep = goal.substeps.find(s => s.id === substepId);                 if (substep) {                   substep.completed = completed;                   localStorage.setItem('taskpixel_data', JSON.stringify(data));                    // é‡æ–°æ¸²æŸ“ä»¥æ›´æ–°è¿›åº¦                   renderGoals(task.goals, taskId);                   updateProgressBar(taskId);                 }               }             }           }         } catch (e) {           alert('æ›´æ–°å­æ­¥éª¤çŠ¶æ€å¤±è´¥: ' + e.message);         }       }        // å†…è”æ ‡ç­¾ç®¡ç†å™¨       const InlineTagManager = {         activeInput: null,          init: function () {           this.bindEvents();           console.log('å†…è”æ ‡ç­¾ç®¡ç†å™¨åˆå§‹åŒ–å®Œæˆ');         },          bindEvents: function () {           // +Tags æŒ‰é’®ç‚¹å‡»äº‹ä»¶           document.addEventListener('click', (e) => {             if (e.target.classList.contains('add-tags-btn')) {               this.showTagInput(e.target);             }           });            // è¾“å…¥æ¡†è¾“å…¥äº‹ä»¶           document.addEventListener('input', (e) => {             if (e.target.classList.contains('tag-input-field')) {               this.handleTagInput(e.target);             }           });            // é”®ç›˜äº‹ä»¶           document.addEventListener('keydown', (e) => {             if (e.target.classList.contains('tag-input-field')) {               this.handleKeydown(e);             }           });            // ç‚¹å‡»å…¶ä»–åœ°æ–¹å…³é—­è¾“å…¥æ¡†           document.addEventListener('click', (e) => {             if (!e.target.closest('.tags-input-container')) {               this.hideAllTagInputs();             }           });         },          // æ˜¾ç¤ºæ ‡ç­¾è¾“å…¥æ¡†         showTagInput: function (button) {           this.hideAllTagInputs();            const container = button.closest('.tags-input-container');           const wrapper = container.querySelector('.tag-input-wrapper');           const input = wrapper.querySelector('.tag-input-field');            button.classList.add('hidden');           wrapper.classList.remove('hidden');            setTimeout(() => {             input.focus();             this.activeInput = input;           }, 50);         },          // éšè—æ‰€æœ‰æ ‡ç­¾è¾“å…¥æ¡†         hideAllTagInputs: function () {           document.querySelectorAll('.tags-input-container').forEach(container => {             const button = container.querySelector('.add-tags-btn');             const wrapper = container.querySelector('.tag-input-wrapper');             const input = wrapper.querySelector('.tag-input-field');             const suggestions = wrapper.querySelector('.tag-suggestions');              button.classList.remove('hidden');             wrapper.classList.add('hidden');             suggestions.classList.add('hidden');             input.value = '';           });            this.activeInput = null;         },          // å¤„ç†æ ‡ç­¾è¾“å…¥         handleTagInput: function (input) {           const query = input.value.trim();           if (query.length === 0) {             this.hideSuggestions(input);             return;           }            // è·å–ç°æœ‰æ ‡ç­¾è¿›è¡Œæœç´¢           const existingTags = this.getAllTags();           const filteredTags = existingTags.filter(tag =>             tag.name.toLowerCase().includes(query.toLowerCase())           );            this.showSuggestions(input, filteredTags, query);         },          // å¤„ç†é”®ç›˜äº‹ä»¶         handleKeydown: function (e) {           const input = e.target;           const suggestions = input.parentElement.querySelector('.tag-suggestions');            if (e.key === 'Escape') {             this.hideAllTagInputs();             return;           }            if (e.key === 'Enter') {             e.preventDefault();             const selectedItem = suggestions.querySelector('.tag-suggestion-item.selected');              if (selectedItem) {               if (selectedItem.dataset.tagId === 'create-new') {                 this.createAndAddTag(input, selectedItem.dataset.tagName);               } else {                 this.selectTag(input, selectedItem.dataset.tagId);               }             } else {               const query = input.value.trim();               if (query) {                 this.createAndAddTag(input, query);               }             }             return;           }            if (e.key === 'ArrowDown' || e.key === 'ArrowUp') {             e.preventDefault();             this.navigateSuggestions(suggestions, e.key === 'ArrowDown');             return;           }         },          // æ˜¾ç¤ºå»ºè®®         showSuggestions: function (input, tags, query) {           const suggestions = input.parentElement.querySelector('.tag-suggestions');           suggestions.innerHTML = '';            // æ·»åŠ ç°æœ‰æ ‡ç­¾å»ºè®®           tags.forEach(tag => {             const item = document.createElement('div');             item.className = 'tag-suggestion-item';             item.dataset.tagId = tag.id;             // ä½¿ç”¨ fallback æœºåˆ¶ç¡®ä¿æ ‡ç­¾æ˜¾ç¤º             const displayText = tag.display_text || '#' + tag.name || '#' + tag.id;             item.innerHTML = `<span style="color: ${tag.color || '#374151'}">${displayText}</span>`;             item.addEventListener('click', () => this.selectTag(input, tag.id));             suggestions.appendChild(item);           });            // æ£€æŸ¥æ˜¯å¦å·²å­˜åœ¨å®Œå…¨åŒ¹é…çš„æ ‡ç­¾           const existingTags = this.getAllTags();           const exactMatch = existingTags.find(tag =>             tag.name.toLowerCase() === query.toLowerCase()           );            // å¦‚æœæ²¡æœ‰åŒ¹é…çš„æ ‡ç­¾ä¸”æ²¡æœ‰å®Œå…¨åŒ¹é…çš„æ ‡ç­¾ï¼Œæ˜¾ç¤ºåˆ›å»ºæ–°æ ‡ç­¾é€‰é¡¹           if (tags.length === 0 && query && !exactMatch) {             const createItem = document.createElement('div');             createItem.className = 'tag-suggestion-item';             createItem.dataset.tagId = 'create-new';             createItem.dataset.tagName = query;             createItem.innerHTML = `åˆ›å»ºæ–°æ ‡ç­¾: <strong>#${query}</strong>`;             createItem.addEventListener('click', () => this.createAndAddTag(input, query));             suggestions.appendChild(createItem);           } else if (exactMatch && tags.length === 0) {             // å¦‚æœæœ‰å®Œå…¨åŒ¹é…ä½†ä¸åœ¨è¿‡æ»¤ç»“æœä¸­ï¼Œæ˜¾ç¤ºè¯¥æ ‡ç­¾             const item = document.createElement('div');             item.className = 'tag-suggestion-item';             item.dataset.tagId = exactMatch.id;             item.innerHTML = `<span style="color: ${exactMatch.color || '#374151'}">#${exactMatch.name}</span>`;             item.addEventListener('click', () => this.selectTag(input, exactMatch.id));             suggestions.appendChild(item);           }            suggestions.classList.remove('hidden');         },          // éšè—å»ºè®®         hideSuggestions: function (input) {           const suggestions = input.parentElement.querySelector('.tag-suggestions');           suggestions.classList.add('hidden');         },          // å¯¼èˆªå»ºè®®åˆ—è¡¨         navigateSuggestions: function (suggestions, isDown) {           const items = suggestions.querySelectorAll('.tag-suggestion-item');           if (items.length === 0) return;            const currentSelected = suggestions.querySelector('.tag-suggestion-item.selected');           let newIndex = 0;            if (currentSelected) {             currentSelected.classList.remove('selected');             const currentIndex = Array.from(items).indexOf(currentSelected);              if (isDown) {               newIndex = (currentIndex + 1) % items.length;             } else {               newIndex = currentIndex === 0 ? items.length - 1 : currentIndex - 1;             }           }            items[newIndex].classList.add('selected');         },          // é€‰æ‹©æ ‡ç­¾         selectTag: function (input, tagId) {           const container = input.closest('.tags-input-container');           const target = container.dataset.target;           const taskId = container.dataset.taskId;           const goalId = container.dataset.goalId;            if (target === 'task') {             this.addTagToTask(taskId, tagId);           } else if (target === 'goal') {             this.addTagToGoal(taskId, goalId, tagId);           }            this.hideAllTagInputs();         },          // åˆ›å»ºå¹¶æ·»åŠ æ–°æ ‡ç­¾         createAndAddTag: function (input, tagName) {           // ä½¿ç”¨TagManageråˆ›å»ºæ–°æ ‡ç­¾           if (window.TaskPixel && window.TaskPixel.TagManager) {             const result = window.TaskPixel.TagManager.createTag({ name: tagName });             if (result.success) {               this.selectTag(input, result.tagId);             } else if (result.reason === 'tag_exists') {               this.selectTag(input, result.tagId);             } else {               alert('åˆ›å»ºæ ‡ç­¾å¤±è´¥ï¼Œè¯·é‡è¯•');             }           } else {             // å¤‡ç”¨æ–¹æ¡ˆï¼šç›´æ¥åˆ›å»ºç®€å•æ ‡ç­¾             const cleanTagName = tagName.replace(/^#/, "").trim();              // æ£€æŸ¥æ ‡ç­¾æ˜¯å¦å·²å­˜åœ¨             const data = JSON.parse(localStorage.getItem('taskpixel_data') || '{}');             if (!data.tags) data.tags = [];              const existingTag = data.tags.find(tag => tag.name.toLowerCase() === cleanTagName.toLowerCase());             if (existingTag) {               // å¦‚æœæ ‡ç­¾å·²å­˜åœ¨ï¼Œç›´æ¥é€‰æ‹©ç°æœ‰æ ‡ç­¾               this.selectTag(input, existingTag.id);               return;             }              const newTag = {               id: 'tag-' + Date.now(),               name: cleanTagName,               display_text: '#' + cleanTagName,               color: this.getRandomTagColor(),               created_at: new Date().toISOString(),               usage_count: 0             };              data.tags.push(newTag);             localStorage.setItem('taskpixel_data', JSON.stringify(data));              // é€šçŸ¥ DataStore é‡æ–°åŠ è½½æ•°æ®             if (typeof TaskPixel !== 'undefined' && TaskPixel.DataStore) {               TaskPixel.DataStore.loadFromStorage();             }              // ä½¿ TagManager ç¼“å­˜å¤±æ•ˆ             if (typeof TaskPixel !== 'undefined' && TaskPixel.TagManager) {               TaskPixel.TagManager.invalidateCache();             }              this.selectTag(input, newTag.id);           }         },          // è·å–æ‰€æœ‰æ ‡ç­¾         getAllTags: function () {           try {             const data = JSON.parse(localStorage.getItem('taskpixel_data') || '{}');             return data.tags || [];           } catch (e) {             return [];           }         },          // è·å–éšæœºæ ‡ç­¾é¢œè‰²         getRandomTagColor: function () {           const colors = ['#374151', '#6B7280', '#4B5563', '#1F2937', '#065F46', '#7C2D12', '#581C87', '#92400E'];           return colors[Math.floor(Math.random() * colors.length)];         },          // æ·»åŠ æ ‡ç­¾åˆ°ä»»åŠ¡         addTagToTask: function (taskId, tagId) {           try {             const data = JSON.parse(localStorage.getItem('taskpixel_data') || '{}');             const task = data.tasks?.find(t => t.id === taskId);              if (task) {               if (!task.tags) task.tags = [];               if (!task.tags.includes(tagId)) {                 task.tags.push(tagId);                 localStorage.setItem('taskpixel_data', JSON.stringify(data));                  // é€šçŸ¥ DataStore é‡æ–°åŠ è½½æ•°æ®                 if (typeof TaskPixel !== 'undefined' && TaskPixel.DataStore) {                   TaskPixel.DataStore.loadFromStorage();                 }                  this.renderTaskTags(taskId);               }             }           } catch (e) {             console.error('æ·»åŠ ä»»åŠ¡æ ‡ç­¾å¤±è´¥:', e);           }         },          // æ·»åŠ æ ‡ç­¾åˆ°