n (taskId, tagId) {           try {             // ä½¿ç”¨ DataStore API è€Œä¸æ˜¯ç›´æ¥æ“ä½œ localStorage             const task = TaskPixel.DataStore.getTaskById(taskId);              if (task && task.tags) {               task.tags = task.tags.filter(id => id !== tagId);               // ä½¿ç”¨ DataStore çš„ä¿å­˜æ–¹æ³•ï¼Œç¡®ä¿æ•°æ®åŒæ­¥               TaskPixel.DataStore.saveToStorage();               this.renderTaskTags(taskId);                // è§¦å‘äº‹ä»¶é€šçŸ¥å…¶ä»–ç»„ä»¶               TaskPixel.EventBus.emit("task:tags_updated", {                 taskId: taskId,                 tags: task.tags,               });             }           } catch (e) {             console.error('ç§»é™¤ä»»åŠ¡æ ‡ç­¾å¤±è´¥:', e);           }         },          // ä»ç›®æ ‡ç§»é™¤æ ‡ç­¾         removeTagFromGoal: function (goalId, tagId) {           try {             // ä½¿ç”¨ DataStore API è€Œä¸æ˜¯ç›´æ¥æ“ä½œ localStorage             const tasks = TaskPixel.DataStore.getAllTasks();              // æ‰¾åˆ°å¹¶æ›´æ–°ç›®æ ‡             for (const task of tasks) {               const goal = task.goals?.find(g => g.id === goalId);               if (goal && goal.tags) {                 goal.tags = goal.tags.filter(id => id !== tagId);                 // ä½¿ç”¨ DataStore çš„ä¿å­˜æ–¹æ³•ï¼Œç¡®ä¿æ•°æ®åŒæ­¥                 TaskPixel.DataStore.saveToStorage();                 this.renderGoalTags(goalId);                  // è§¦å‘äº‹ä»¶é€šçŸ¥å…¶ä»–ç»„ä»¶                 TaskPixel.EventBus.emit("goal:tags_updated", {                   goalId: goalId,                   tags: goal.tags,                 });                 break;               }             }           } catch (e) {             console.error('ç§»é™¤ç›®æ ‡æ ‡ç­¾å¤±è´¥:', e);           }         }       };        // å†…è”æ ‡ç­¾ç®¡ç†å™¨åˆå§‹åŒ–å°†åœ¨å®šä¹‰åè¿›è¡Œ        // æ›´æ–°è¿›åº¦æ¡       function updateProgressBar(taskId) {         if (!taskId) {           console.warn('updateProgressBar: taskId is missing');           return;         }          setTimeout(() => {           try {             // è·å–å®é™…è¿›åº¦             let actualProgress = 0;              if (window.TaskPixel?.DataStoreAdapter?.getTaskProgress) {               actualProgress = window.TaskPixel.DataStoreAdapter.getTaskProgress(taskId);             } else {               // å¤‡ç”¨è®¡ç®—æ–¹æ³•               const raw = localStorage.getItem('taskpixel_data');               if (raw) {                 const data = JSON.parse(raw);                 const task = data.tasks?.find(t => t.id === taskId);                 if (task) {                   // è®¡ç®—åŸºäºç›®æ ‡å’Œå­æ­¥éª¤çš„è¿›åº¦                   let totalSubsteps = 0;                   let completedSubsteps = 0;                    if (task.goals && Array.isArray(task.goals)) {                     task.goals.forEach(goal => {                       if (goal.substeps && Array.isArray(goal.substeps)) {                         goal.substeps.forEach(substep => {                           totalSubsteps++;                           if (substep.completed) {                             completedSubsteps++;                           }                         });                       }                     });                   }                    actualProgress = totalSubsteps > 0 ? Math.round((completedSubsteps / totalSubsteps) * 100) : 0;                 }               }             }              // æ›´æ–°è¿›åº¦æ¡             const progressEl = document.querySelector('.progress-bar-fill');             const progressTextEl = document.querySelector('.progress-text');              if (progressEl) {               progressEl.style.width = actualProgress + '%';             }             if (progressTextEl) {               progressTextEl.textContent = actualProgress + '% å®Œæˆ';             }             // æ›´æ–°å†…éƒ¨æ ‡ç­¾ï¼ˆè‹¥å­˜åœ¨ï¼‰             const progressLabelEl = document.querySelector('.progress-label');             if (progressLabelEl) {               progressLabelEl.textContent = actualProgress + '%';               if (actualProgress < 10) {                 progressLabelEl.classList.add('hidden-small');               } else {                 progressLabelEl.classList.remove('hidden-small');               }             }            } catch (e) {             console.error('Error updating progress bar:', e);           }         }, 100);       }        // åˆå§‹åŒ–å†…è”æ ‡ç­¾ç®¡ç†å™¨       InlineTagManager.init();        // åˆå§‹åŒ–æ ‡ç­¾é¡µåŠŸèƒ½       initTabSystem();        // åˆå§‹åŒ–èµ„æºç®¡ç†åŠŸèƒ½       initResourceManagement();      });      // æ ‡ç­¾é¡µç³»ç»Ÿ     function initTabSystem() {       const tabButtons = document.querySelectorAll('.tab-btn');       const tabContents = document.querySelectorAll('.tab-content');        tabButtons.forEach(button => {         button.addEventListener('click', function () {           const targetTab = this.getAttribute('data-tab');            // ç§»é™¤æ‰€æœ‰æ´»è·ƒçŠ¶æ€           tabButtons.forEach(btn => btn.classList.remove('active'));           tabContents.forEach(content => content.classList.add('hidden'));            // æ¿€æ´»å½“å‰æ ‡ç­¾é¡µ           this.classList.add('active');           const targetContent = document.querySelector(`.tab-content[data-tab="${targetTab}"]`);           if (targetContent) {             targetContent.classList.remove('hidden');           }            // å¦‚æœåˆ‡æ¢åˆ°èµ„æºæ ‡ç­¾é¡µï¼Œåˆ·æ–°èµ„æºåˆ—è¡¨           if (targetTab === 'resources') {             refreshResourceList();           }         });       });     }      // èµ„æºç®¡ç†ç³»ç»Ÿ     function initResourceManagement() {       const addButtons = document.querySelectorAll('.resource-add-btn');        addButtons.forEach(button => {         button.addEventListener('click', function () {           const uploadMethod = this.getAttribute('data-method');           showAddResourceDialog(uploadMethod);         });       });        // åˆå§‹åŒ–æ—¶åˆ·æ–°èµ„æºåˆ—è¡¨       refreshResourceList();     }      // æ˜¾ç¤ºæ·»åŠ èµ„æºå¯¹è¯æ¡†     function showAddResourceDialog(method) {       if (method === 'local') {         // æœ¬åœ°æ–‡ä»¶ä¸Šä¼          showLocalFileDialog();       } else if (method === 'url') {         // URLé“¾æ¥ä¸Šä¼          showUrlDialog();       }     }      // æœ¬åœ°æ–‡ä»¶ä¸Šä¼ å¯¹è¯æ¡†     function showLocalFileDialog() {       // åˆ›å»ºæ–‡ä»¶è¾“å…¥å…ƒç´        const fileInput = document.createElement('input');       fileInput.type = 'file';       fileInput.style.display = 'none';       fileInput.accept = '*/*'; // æ¥å—æ‰€æœ‰ç±»å‹çš„æ–‡ä»¶        fileInput.addEventListener('change', function (e) {         const file = e.target.files[0];         if (file) {           const name = prompt('è¯·è¾“å…¥èµ„æºåç§°:', file.name);           if (name) {             // æ ¹æ®æ–‡ä»¶ç±»å‹è‡ªåŠ¨åˆ¤æ–­èµ„æºç±»å‹             const resourceType = getResourceTypeFromFile(file);             // æœ¬åœ°æ–‡ä»¶ï¼Œè®°å½•æ–‡ä»¶è·¯å¾„ï¼ˆå®é™…é¡¹ç›®ä¸­å¯èƒ½éœ€è¦ä¸Šä¼ åˆ°æœåŠ¡å™¨ï¼‰             const filePath = file.name; // ç®€åŒ–å¤„ç†ï¼Œå®é™…åº”è¯¥æ˜¯å®Œæ•´è·¯å¾„             addResource(resourceType, name, filePath, false, file);           }         }         document.body.removeChild(fileInput);       });        document.body.appendChild(fileInput);       fileInput.click();     }      // URLé“¾æ¥ä¸Šä¼ å¯¹è¯æ¡†     function showUrlDialog() {       const name = prompt('è¯·è¾“å…¥èµ„æºåç§°:');       if (!name) return;        const url = prompt('è¯·è¾“å…¥èµ„æºçš„URLé“¾æ¥:\n\nç¤ºä¾‹:\nâ€¢ å›¾ç‰‡: https://example.com/image.jpg\nâ€¢ æ–‡ä»¶: https://example.com/file.pdf\nâ€¢ ç½‘ç«™: https://example.com');       if (!url) return;        // ç®€å•çš„URLéªŒè¯       if (!isValidUrl(url)) {         alert('è¯·è¾“å…¥æœ‰æ•ˆçš„URLåœ°å€ï¼');         return;       }        // æ ¹æ®URLè‡ªåŠ¨åˆ¤æ–­èµ„æºç±»å‹       const resourceType = getResourceTypeFromUrl(url);       addResource(resourceType, name, url, true);     }      // URLéªŒè¯å‡½æ•°     function isValidUrl(string) {       try {         new URL(string);         return true;       } catch (_) {         return false;       }     }      // æ ¹æ®æ–‡ä»¶ç±»å‹åˆ¤æ–­èµ„æºç±»å‹     function getResourceTypeFromFile(file) {       const mimeType = file.type.toLowerCase();       const fileName = file.name.toLowerCase();        if (mimeType.startsWith('image/')) {         return 'image';       } else if (mimeType.includes('pdf') || fileName.endsWith('.pdf') ||         mimeType.includes('document') || fileName.match(/\.(doc|docx|txt|md|rtf)$/)) {         return 'document';       } else if (fileName.match(/\.(html|htm)$/)) {         return 'link';       } else {         return 'file';       }     }      // æ ¹æ®URLåˆ¤æ–­èµ„æºç±»å‹     function getResourceTypeFromUrl(url) {       const urlLower = url.toLowerCase();        if (urlLower.match(/\.(jpg|jpeg|png|gif|bmp|svg|webp)($|\?)/)) {         return 'image';       } else if (urlLower.match(/\.(pdf|doc|docx|txt|md|rtf)($|\?)/)) {         return 'document';       } else if (urlLower.match(/\.(html|htm)($|\?)/)) {         return 'link';       } else {         return 'file';       }     }      // æ·»åŠ èµ„æº     function addResource(type, name, path, isUrl = false, fileObj = null) {       try {         const taskId = new URLSearchParams(window.location.search).get('id');         if (!taskId) {           alert('æœªæ‰¾åˆ°ä»»åŠ¡ID');           return;         }          const data = JSON.parse(localStorage.getItem('taskpixel_data') || '{}');         const task = data.tasks?.find(t => t.id === taskId);          if (!task) {           alert('æœªæ‰¾åˆ°ä»»åŠ¡');           return;         }          // åˆå§‹åŒ–èµ„æºæ•°ç»„         if (!task.resources) {           task.resources = [];         }          // æ·»åŠ æ–°èµ„æº         const resource = {           id: 'resource_' + Date.now(),           type: type,           name: name,           path: path,           isUrl: isUrl,           addDate: new Date().toISOString(),           size: fileObj ? fileObj.size : null,           fileType: fileObj ? fileObj.type : null         };          task.resources.push(resource);          // ä¿å­˜æ•°æ®         localStorage.setItem('taskpixel_data', JSON.stringify(data));          // åˆ·æ–°æ˜¾ç¤º         refreshResourceList();          const methodText = isUrl ? 'URLé“¾æ¥' : 'æœ¬åœ°æ–‡ä»¶';         alert(`${methodText}æ·»åŠ æˆåŠŸï¼`);        } catch (e) {         console.error('æ·»åŠ èµ„æºå¤±è´¥:', e);         alert('æ·»åŠ èµ„æºå¤±è´¥: ' + e.message);       }     }      // åˆ é™¤èµ„æº     function deleteResource(resourceId) {       if (!confirm('ç¡®å®šè¦åˆ é™¤è¿™ä¸ªèµ„æºå—ï¼Ÿ')) {         return;       }        try {         const taskId = new URLSearchParams(window.location.search).get('id');         if (!taskId) return;          const data = JSON.parse(localStorage.getItem('taskpixel_data') || '{}');         const task = data.tasks?.find(t => t.id === taskId);          if (!task || !task.resources) return;          // åˆ é™¤èµ„æº         task.resources = task.resources.filter(r => r.id !== resourceId);          // ä¿å­˜æ•°æ®         localStorage.setItem('taskpixel_data', JSON.stringify(data));          // åˆ·æ–°æ˜¾ç¤º         refreshResourceList();          alert('èµ„æºåˆ é™¤æˆåŠŸï¼');        } catch (e) {         console.error('åˆ é™¤èµ„æºå¤±è´¥:', e);         alert('åˆ é™¤èµ„æºå¤±è´¥: ' + e.message);       }     }      // åˆ·æ–°èµ„æºåˆ—è¡¨     function refreshResourceList() {       try {         const taskId = new URLSearchParams(window.location.search).get('id');         if (!taskId) return;          const data = JSON.parse(localStorage.getItem('taskpixel_data') || '{}');         const task = data.tasks?.find(t => t.id === taskId);          const resourceList = document.getElementById('resourceList');         const emptyState = document.getElementById('emptyResourcesState');          if (!resourceList || !emptyState) return;          // æ¸…ç©ºåˆ—è¡¨         resourceList.innerHTML = '';          if (!task || !task.resources || task.resources.length === 0) {           // æ˜¾ç¤ºç©ºçŠ¶æ€           emptyState.style.display = 'block';           return;         }          // éšè—ç©ºçŠ¶æ€         emptyState.style.display = 'none';          // èµ„æºå›¾æ ‡æ˜ å°„         const resourceIcons = {           image: 'ğŸ“·',           file: 'ğŸ“',           document: 'ğŸ“„',           link: 'ğŸ”—'         };          // æ¸²æŸ“èµ„æºåˆ—è¡¨         task.resources.forEach(resource => {           const resourceItem = document.createElement('div');           resourceItem.className = 'resource-item';            // æ ¹æ®èµ„æºç±»å‹æ„å»ºä¸åŒçš„HTMLç»“æ„           const isClickable = resource.isUrl;           const sizeText = resource.size ? formatFileSize(resource.size) : '';           const typeText = resource.fileType || '';            resourceItem.innerHTML = `             <div class="resource-item-header">               <div class="resource-icon">${resourceIcons[resource.type] || 'ğŸ“'}</div>               <div class="resource-info flex-1" ${isClickable ? `style="cursor: pointer;" onclick="openResource('${resource.path}')"` : ''}>                 <div class="resource-name" style="${isClickable ? 'color: #3b82f6; text-decoration: underline;' : ''}">${resource.name}</div>                 <div class="resource-path">${resource.path}</div>                 <div class="text-xs text-gray-400 mt-1 flex gap-2">                   <span>${resource.isUrl ? 'ğŸ”— URLé“¾æ¥' : 'ğŸ’¾ æœ¬åœ°æ–‡ä»¶'}</span>                   <span>â€¢</span>                   <span>${new Date(resource.addDate).toLocaleDateString()}</span>                   ${sizeText ? `<span>â€¢ ${sizeText}</span>` : ''}                   ${typeText ? `<span>â€¢ ${typeText}</span>` : ''}                 </div>                 ${isClickable ? '<div class="text-xs text-blue-500 mt-1">ğŸ‘† ç‚¹å‡»è®¿é—®é“¾æ¥</div>' : ''}               </div>               <div class="flex gap-2">                 ${isClickable ? `<button class="pixel-button bg-blue-500 text-white text-xs px-2 py-1" onclick="openResource('${resource.path}')">è®¿é—®</button>` : ''}                 <button class="resource-delete pixel-button bg-red-500 text-white text-xs px-2 py-1" onclick="deleteResource('${resource.id}')">                   åˆ é™¤                 </button>               </div>             </div>           `;            resourceList.appendChild(resourceItem);         });        } catch (e) {         console.error('åˆ·æ–°èµ„æºåˆ—è¡¨å¤±è´¥:', e);       }     }      // æ–‡ä»¶å¤§å°æ ¼å¼åŒ–     function formatFileSize(bytes) {       if (bytes === 0) return '0 Bytes';       const k = 1024;       const sizes = ['Bytes', 'KB', 'MB', 'GB'];       const i = Math.floor(Math.log(bytes) / Math.log(k));       return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];     }      // æ‰“å¼€èµ„æºé“¾æ¥     function openResource(url) {       if (!url) return;        try {         // åœ¨æ–°æ ‡ç­¾é¡µä¸­æ‰“å¼€é“¾æ¥         window.open(url, '_blank', 'noopener,noreferrer');       } catch (e) {         console.error('æ‰“å¼€é“¾æ¥å¤±è´¥:', e);         alert('æ— æ³•æ‰“å¼€é“¾æ¥: ' + url);       }     }      // å°†å‡½æ•°è®¾ä¸ºå…¨å±€ï¼Œä»¥ä¾¿åœ¨onclickä¸­è°ƒç”¨     window.deleteResource = deleteResource;     window.openResource = openResource;    </script> </body>  </html> = Get-Content "task_detail.html" -Encoding UTF8
