     <!-- ç›®æ ‡å’Œå­æ­¥éª¤ -->             <div class="pixel-border p-6 panel-bg">               <div class="space-y-6 goals-container">                 <!-- ç›®æ ‡å’Œå­æ­¥éª¤å°†é€šè¿‡JavaScriptåŠ¨æ€æ¸²æŸ“ -->               </div>             </div>           </div>            <!-- å³ä¾§æ  -->           <div class="space-y-8">             <!-- è®°å½•å·¥ä½œè¿›åº¦ -->             <div class="pixel-border p-6 panel-bg">               <h3 class="font-display text-2xl text-text-primary mb-6">è®°å½•å·¥ä½œè¿›åº¦</h3>               <form id="timeline-form" class="space-y-6">                 <div>                   <label class="block font-display text-lg text-text-primary mb-2" for="work-content">å·¥ä½œå†…å®¹</label>                   <textarea id="work-content" placeholder="æè¿°æ‚¨çš„å·¥ä½œ..." rows="4"></textarea>                 </div>                 <div>                   <label class="block font-display text-lg text-text-primary mb-2" for="time-spent">æ—¶é—´ (å°æ—¶)</label>                   <input id="time-spent" placeholder="å¦‚ï¼š2" type="number" step="0.1" min="0.1" />                 </div>                 <div>                   <button type="submit"                     class="pixel-button w-full bg-accent-green text-white font-display py-3 px-4 text-sm flex items-center justify-center gap-2"                     title="è®°å½•è¿›åº¦">                     <span>ğŸ’¾</span>                     <span>è®°å½•è¿›åº¦</span>                   </button>                 </div>               </form>             </div>              <!-- è¿›åº¦å†å² -->             <div class="pixel-border p-6 panel-bg">               <h3 class="font-display text-2xl text-text-primary mb-6">è¿›åº¦å†å²</h3>               <div class="space-y-4 timeline-container">                 <!-- æ—¶é—´çº¿è®°å½•å°†é€šè¿‡JavaScriptåŠ¨æ€æ¸²æŸ“ -->               </div>             </div>           </div>          </div>       </div>     </main>   </div>    <script>     // ç¡®ä¿ TaskPixel åç§°ç©ºé—´å­˜åœ¨ï¼Œå³ä½¿ core.js æœªèƒ½æ­£ç¡®åŠ è½½     window.TaskPixel = window.TaskPixel || {};      // ç»Ÿä¸€çš„ç¼–è¾‘ä»»åŠ¡å‡½æ•°     function editTask(taskId) {       if (!taskId) {         alert('æœªæ‰¾åˆ°ä»»åŠ¡ID');         return;       }        let data, task;        try {         const raw = localStorage.getItem('taskpixel_data');         if (!raw) {           alert('æœªæ‰¾åˆ°ä»»åŠ¡æ•°æ®');           return;         }          data = JSON.parse(raw);         task = data.tasks.find(t => t.id === taskId);          if (!task) {           alert('æœªæ‰¾åˆ°æŒ‡å®šä»»åŠ¡');           return;         }        } catch (parseError) {         console.error('è¯»å–ä»»åŠ¡æ•°æ®é”™è¯¯:', parseError);         alert('è¯»å–ä»»åŠ¡æ•°æ®å¤±è´¥: ' + parseError.message);         return;       }        try {         // åˆ›å»ºç¼–è¾‘å¯¹è¯æ¡†         const dialogElement = document.createElement('div');         dialogElement.className = 'fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50';         dialogElement.id = 'edit-task-dialog';          // è®¡ç®—å½“å‰è¿›åº¦ï¼Œå†³å®šçŠ¶æ€é€‰é¡¹         let currentProgress = window.TaskPixel?.DataStoreAdapter?.getTaskProgress(taskId) || task.progress || 0;         let currentStatus = task.status || 'in-progress';         let isCompleted = currentProgress >= 100;          dialogElement.innerHTML = `           <div class="pixel-border bg-white p-6 w-full max-w-lg">             <h2 class="text-2xl font-display mb-6">ç¼–è¾‘ä»»åŠ¡</h2>             <form id="edit-task-form">               <div class="mb-4">                 <label class="block font-display text-lg mb-2" for="edit-task-title">ä»»åŠ¡æ ‡é¢˜</label>                 <input type="text" id="edit-task-title" class="w-full" required placeholder="è¾“å…¥ä»»åŠ¡æ ‡é¢˜" value="${task.title || ''}">               </div>               <div class="mb-4">                 <label class="block font-display text-lg mb-2" for="edit-task-description">ä»»åŠ¡æè¿°</label>                 <textarea id="edit-t