'æœªæ‰¾åˆ°ä»»åŠ¡ID');         return;       }        try {         const raw = localStorage.getItem('taskpixel_data');         if (!raw) {           alert('æœªæ‰¾åˆ°ä»»åŠ¡æ•°æ®');           return;         }          const data = JSON.parse(raw);         const task = data.tasks.find(t => t.id === taskId);          if (!task) {           alert('æœªæ‰¾åˆ°æŒ‡å®šä»»åŠ¡');           return;         }          // åˆ‡æ¢å®ŒæˆçŠ¶æ€         const oldStatus = task.status;         task.status = task.status === 'completed' ? 'in-progress' : 'completed';          // å¦‚æœæ ‡è®°ä¸ºå®Œæˆï¼Œè®¾ç½®è¿›åº¦ä¸º100%         if (task.status === 'completed') {           task.progress = 100;         }          // ä¿å­˜åˆ°localStorage         localStorage.setItem('taskpixel_data', JSON.stringify(data));          // æ˜¾ç¤ºæˆåŠŸæ¶ˆæ¯         const statusText = task.status === 'completed' ? 'å·²å®Œæˆ' : 'è¿›è¡Œä¸­';         alert(`ä»»åŠ¡çŠ¶æ€å·²æ›´æ–°ä¸ºï¼š${statusText}`);          // åˆ·æ–°é¡µé¢         location.reload();        } catch (e) {         console.error('æ›´æ–°ä»»åŠ¡çŠ¶æ€é”™è¯¯:', e);         alert('æ›´æ–°ä»»åŠ¡çŠ¶æ€æ—¶å‡ºé”™: ' + e.message);       }     }      // æ·»åŠ å·¥ä½œè¿›åº¦è®°å½•     function addWorkProgress(taskId, content, hours) {       if (!taskId) {         alert('æœªæ‰¾åˆ°ä»»åŠ¡ID');         return;       }        try {         const raw = localStorage.getItem('taskpixel_data');         if (!raw) {           alert('æœªæ‰¾åˆ°ä»»åŠ¡æ•°æ®');           return;         }          const data = JSON.parse(raw);         const task = data.tasks.find(t => t.id === taskId);          if (!task) {           alert('æœªæ‰¾åˆ°æŒ‡å®šä»»åŠ¡');           return;         }          // åˆå§‹åŒ–timelineæ•°ç»„         if (!task.timeline) {           task.timeline = [];         }          // åˆ›å»ºæ–°çš„è¿›åº¦è®°å½•         const progressEntry = {           id: 'progress-' + Date.now(),           date: new Date().toISOString(),           content: content,           hours: hours,           timestamp: Date.now()         };          task.timeline.push(progressEntry);          // æ›´æ–°æ€»å·¥ä½œæ—¶é—´         const totalHours = task.timeline.reduce((sum, entry) => sum + (entry.hours || 0), 0);         task.totalHours = totalHours;          // ä¿å­˜åˆ°localStorage         localStorage.setItem('taskpixel_data', JSON.stringify(data));          // æ¸…ç©ºè¡¨å•         document.getElementById('work-content').value = '';         document.getElementById('time-spent').value = '';          // é‡æ–°æ¸²æŸ“è¿›åº¦å†å²         renderProgressHistory(task.timeline);          alert('å·¥ä½œè¿›åº¦è®°å½•æˆåŠŸï¼');        } catch (e) {         alert('è®°å½•å·¥ä½œè¿›åº¦æ—¶å‡ºé”™: ' + e.message);       }     }      // æ¸²æŸ“è¿›åº¦å†å²     function renderProgressHistory(timeline) {       const container = document.querySelector('.timeline-container');       if (!container) return;        container.innerHTML = '';        if (!timeline || timeline.length === 0) {         container.innerHTML = `           <div class="text-center py-8">             <p class="text-gray-500 mb-4">è¿˜æ²¡æœ‰å·¥ä½œè¿›åº¦è®°å½•</p>             <p class="text-gray-400 text-sm">å¼€å§‹è®°å½•æ‚¨çš„å·¥ä½œè¿›åº¦å§ï¼</p>           </div>         `;         return;       }        // æŒ‰æ—¶é—´å€’åºæ’åˆ—       const sortedTimeline = [...timeline].sort((a, b) => b.timestamp - a.timestamp);        sortedTimeline.forEach(entry => {         const entryElement = document.createElement('div');         entryElement.className = 'pixel-border bg-white p-4 mb-4 hover:shadow-lg transition-shadow';          const date = new Date(entry.date);         const formattedDate = date.toLocaleDateString('zh-CN') + ' ' + date.toLocaleTimeString('zh-CN', { hour: '2-digit', minute: '2-digit' });          entryElement.innerHTML = `           <div class="flex justify-between items-start mb-3">             <div class="flex items-center gap-2">               <div class="w-3 h-3 bg-primary"></div>               <span class="font-display text-sm t