   const data = JSON.parse(raw);           const task = data.tasks.find(t => t.id === taskId);           if (!task || !task.goals) return;            // é‡æ–°æ’åºç›®æ ‡           const reorderedGoals = [];           newOrder.forEach((goalId, index) => {             const goal = task.goals.find(g => g.id === goalId);             if (goal) {               goal.order = index;               reorderedGoals.push(goal);             }           });            task.goals = reorderedGoals;           localStorage.setItem('taskpixel_data', JSON.stringify(data));            console.log('ç›®æ ‡æ’åºå·²æ›´æ–°');         } catch (e) {           console.error('æ›´æ–°ç›®æ ‡æ’åºå¤±è´¥', e);         }       }        // åˆ›å»ºç›®æ ‡å¡ç‰‡       function createGoalCard(goal, taskId) {         const card = document.createElement('div');         card.className = 'border-4 border-black bg-white mb-6 p-4 goal-card';         card.dataset.goalId = goal.id;          // è®¡ç®—è¿›åº¦         const total = goal.substeps ? goal.substeps.length : 0;         const completed = goal.substeps ? goal.substeps.filter(s => s.completed).length : 0;          card.innerHTML = `           <div class="flex items-start justify-between mb-4">               <div class="drag-handle cursor-move text-gray-400 hover:text-gray-600 mr-3 mt-1" title="æ‹–æ‹½æ’åºç›®æ ‡">                 <svg width="14" height="14" viewBox="0 0 12 12" fill="currentColor">                   <circle cx="3" cy="3" r="1"/>                   <circle cx="9" cy="3" r="1"/>                   <circle cx="3" cy="6" r="1"/>                   <circle cx="9" cy="6" r="1"/>                   <circle cx="3" cy="9" r="1"/>                   <circle cx="9" cy="9" r="1"/>                 </svg>               </div>               <div class="flex-1">               <div class="flex items-center justify-between">                 <h4 class="font-display text-lg font-bold mb-2">${goal.title || 'æœªå‘½åç›®æ ‡'}</h4>                 <button class="toggle-substeps-btn