    // æ·»åŠ æ ‡ç­¾åˆ°ç›®æ ‡         addTagToGoal: function (taskId, goalId, tagId) {           try {             const data = JSON.parse(localStorage.getItem('taskpixel_data') || '{}');             const task = data.tasks?.find(t => t.id === taskId);             const goal = task?.goals?.find(g => g.id === goalId);              if (goal) {               if (!goal.tags) goal.tags = [];               if (!goal.tags.includes(tagId)) {                 goal.tags.push(tagId);                 localStorage.setItem('taskpixel_data', JSON.stringify(data));                  // é€šçŸ¥ DataStore é‡æ–°åŠ è½½æ•°æ®                 if (typeof TaskPixel !== 'undefined' && TaskPixel.DataStore) {                   TaskPixel.DataStore.loadFromStorage();                 }                  this.renderGoalTags(goalId);               }             }           } catch (e) {             console.error('æ·»åŠ ç›®æ ‡æ ‡ç­¾å¤±è´¥:', e);           }         },          // æ¸²æŸ“ä»»åŠ¡æ ‡ç­¾         renderTaskTags: function (taskId) {           try {             const data = JSON.parse(localStorage.getItem('taskpixel_data') || '{}');             const task = data.tasks?.find(t => t.id === taskId);             const allTags = data.tags || [];              const container = document.querySelector('.task-tags-display');             if (!container || !task) return;              container.innerHTML = '';              if (task.tags && task.tags.length > 0) {               task.tags.forEach(tagId => {                 const tag = allTags.find(t => t.id === tagId);                 if (tag) {                   const tagElement = this.createTagElement(tag, () => this.removeTagFromTask(taskId, tagId));                   container.appendChild(tagElement);                 }               });             }           } catch (e) {             console.error('æ¸²æŸ“ä»»åŠ¡æ ‡ç­¾å¤±è´¥:', e);           }         },          // æ¸²æŸ“ç›®æ ‡æ ‡ç­¾         renderGoalTags: function (goalId) {           try {             