veTagFromTask: function (taskId, tagId) {           try {             // ä½¿ç”¨ DataStore API è€Œä¸æ˜¯ç›´æ¥æ“ä½œ localStorage             const task = TaskPixel.DataStore.getTaskById(taskId);              if (task && task.tags) {               task.tags = task.tags.filter(id => id !== tagId);               // ä½¿ç”¨ DataStore çš„ä¿å­˜æ–¹æ³•ï¼Œç¡®ä¿æ•°æ®åŒæ­¥               TaskPixel.DataStore.saveToStorage();               this.renderTaskTags(taskId);                // è§¦å‘äº‹ä»¶é€šçŸ¥å…¶ä»–ç»„ä»¶               TaskPixel.EventBus.emit("task:tags_updated", {                 taskId: taskId,                 tags: task.tags,               });             }           } catch (e) {             console.error('ç§»é™¤ä»»åŠ¡æ ‡ç­¾å¤±è´¥:', e);           }         },          // ä»ç›®æ ‡ç§»é™¤æ ‡ç­¾         removeTagFromGoal: function (goalId, tagId) {           try {             // ä½¿ç”¨ DataStore API è€Œä¸æ˜¯ç›´æ¥æ“ä½œ localStorage             const tasks = TaskPixel.DataStore.getAllTasks();              // æ‰¾åˆ°å¹¶æ›´æ–°ç›®æ ‡             for (const task of tasks) {               const goal = task.goals?.find(g => g.id === goalId);               if (goal && goal.tags) {                 goal.tags = goal.tags.filter(id => id !== tagId);                 // ä½¿ç”¨ DataStore çš„ä¿å­˜æ–¹æ³•ï¼Œç¡®ä¿æ•°æ®åŒæ­¥                 TaskPixel.DataStore.saveToStorage();                 this.renderGoalTags(goalId);                  // è§¦å‘äº‹ä»¶é€šçŸ¥å…¶ä»–ç»„ä»¶                 TaskPixel.EventBus.emit("goal:tags_updated", {                   goalId: goalId,                   tags: goal.tags,                 });                 break;               }             }           } catch (e) {             console.error('ç§»é™¤ç›®æ ‡æ ‡ç­¾å¤±è´¥:', e);           }         }       };        // å†…è”æ ‡ç­¾ç®¡ç†å™¨åˆå§‹åŒ–å°†åœ¨å®šä¹‰åè¿›è¡Œ        // æ›´æ–°è¿›åº¦æ¡       function updateProgressBar(taskId) {         if (!taskId) {           console.warn('updateProgressBar: taskId is missing');           return;         }          setTimeout(() => {           try {             // è·å–å®é™…è¿›åº¦             let actualProgress = 0;              if (window.TaskPixel?.DataStoreAdapter?.getTaskProgress) {               actualProgress = window.TaskPixel.DataStoreAdapter.getTaskProgress(taskId);             } else {               // å¤‡ç”¨è®¡ç®—æ–¹æ³•               const raw = localStorage.getItem('taskpixel_data');               if (raw) {                 const data = JSON.parse(raw);                 const task = data.tasks?.find(t => t.id === taskId);                 if (task) {                   // è®¡ç®—åŸºäºç›®æ ‡å’Œå­æ­¥éª¤çš„è¿›åº¦                   let totalSubsteps = 0;                   let completedSubsteps = 0;                    if (task.goals && Array.isArray(task.goals)) {                     task.goals.forEach(goal => {                       if (goal.substeps && Array.isArray(goal.substeps)) {                         goal.substeps.forEach(substep => {                           totalSubsteps++;                           if (substep.completed) {                             completedSubsteps++;                           }                         });                       }                     });                   }                    actualProgress = totalSubsteps > 0 ? Math.round((completedSubsteps / totalSubsteps) * 100) : 0;                 }               }             }              // æ›´æ–°è¿›åº¦æ¡             const progressEl = document.querySelector('.progress-bar-fill');             const progressTextEl = document.querySelector('.progress-text');              if (progressEl) {               progressEl.style.width = actualProgress + '%';             }             if (progressTextEl) {               progressTextEl.textContent = actualProgress + '% å®Œæˆ';             }             // æ›´æ–°å†…éƒ¨æ ‡ç­¾ï¼ˆè‹¥å­˜åœ¨ï¼‰             const progressLabelEl = document.querySelector('.progress-label');             if (progressLabelEl) {               progressLabelEl.textContent = actualProgress + '%';               if (actualProgress < 10) {                 progressLabelEl.classList.add('hidden-small');               } else {                 progressLabelEl.classList.remove('hidden-small');               }             }            } catch (e) {             console.error('Error updating progress bar:', e);           }         }, 100);       }        // åˆå§‹åŒ–å†…è”æ ‡ç­¾ç®¡ç†å™¨       InlineTagManager.init();        // åˆå§‹åŒ–æ ‡ç­¾é¡µåŠŸèƒ½       initTabSystem();        // åˆå§‹åŒ–èµ„æºç®¡ç†åŠŸèƒ½       initResourceManagement();      });      // æ ‡ç­¾é¡µç³»ç»Ÿ     function initTabSystem() {       const tabButtons = document.querySelectorAll('.tab-btn');       const tabContents = document.querySelectorAll('.tab-content');        tabButtons.forEach(button => {         button.addEventListener('click', function () {           const targetTab = this.getAttribute('data-tab');            // ç§»é™¤æ‰€æœ‰æ´»è·ƒçŠ¶æ€           tabButtons.forEach(btn => btn.classList.remove('active'));           tabContents.forEach(content => content.classList.add('hidden'));            // æ¿€æ´»å½“å‰æ ‡ç­¾é¡µ           this.classList.add('active');           const targetContent = document.querySelector(`.tab-content[data-tab="${targetTab}"]`);           if (targetContent) {             targetContent.classList.remove('hidden');           }            // å¦‚æœåˆ‡æ¢åˆ°èµ„æºæ ‡ç­¾é¡µï¼Œåˆ·æ–°èµ„æºåˆ—è¡¨           if (targetTab === 'resources') {             refreshResourceList();           }         });       });     }      // èµ„æºç®¡ç†ç³»ç»Ÿ     function initResourceManagement() {       const addButtons = document.querySelectorAll('.resource-add-btn');        addButtons.forEach(button => {         button.addEventListener('click', function () {           const uploadMethod = this.getAttribute('data-method');           showAddResourceDialog(uploadMethod);         });       });        // åˆå§‹åŒ–æ—¶åˆ·æ–°èµ„æºåˆ—è¡¨       refreshResourceList();     }      // æ˜¾ç¤ºæ·»åŠ èµ„æºå¯¹è¯æ¡†     function showAddResourceDialog(method) {       if (method === 'local') {         // æœ¬åœ°æ–‡ä»¶ä¸Šä¼          showLocalFileDialog();       } else if (method === 'url') {         // URLé“¾æ¥ä¸Šä¼          showUrlDialog();       }     }      // æœ¬åœ°æ–‡ä»¶ä¸Šä¼ å¯¹è¯æ¡†     function showLocalFileDialog() {       // åˆ›å»ºæ–‡ä»¶è¾“å…¥å…ƒç´        const fileInput = document.createElement('input');       fileInput.type = 'file';       fileInput.style.display = 'none';       fileInput.accept = '*/*'; // æ¥å—æ‰€æœ‰ç±»å‹çš„æ–‡ä»¶        fileInput.addEventListener('change', function (e) {         const file = e.target.files[0];         if (file) {           const name = prompt('è¯·è¾“å…¥èµ„æºåç§°:', file.name);           if (name) {             // æ ¹æ®æ–‡ä»¶ç±»å‹è‡ªåŠ¨åˆ¤æ–­èµ„æºç±»å‹             const resourceType = getResourceTypeFromFile(file);             // æœ¬åœ°æ–‡ä»¶ï¼Œè®°å½•æ–‡ä»¶è·¯å¾„ï¼ˆå®é™…é¡¹ç›®ä¸­å¯èƒ½éœ€è¦ä¸Šä¼ åˆ°æœåŠ¡å™¨ï¼‰             const filePath = file.name; // ç®€åŒ–å¤„ç†ï¼Œå®é™…åº”è¯¥æ˜¯å®Œæ•´è·¯å¾„             addResource(resourceType, name, filePath, false, file);           }         }         document.body.removeChild(fileInput);       });        document.body.appendChild(fileInput);       fileInput.click();     }      // URLé“¾æ¥ä¸Šä¼ å¯¹è¯æ¡†     function showUrlDialog() {       const name = prompt('è¯·è¾“å…¥èµ„æºåç§°:');       if (!name) return;        const url = prompt('è¯·è¾“å…¥èµ„æºçš„URLé“¾æ¥:\n\nç¤ºä¾‹:\nâ€¢ å›¾ç‰‡: https://example.com/image.jpg\nâ€¢ æ–‡ä»¶: https://example.com/file.pdf\nâ€¢ ç½‘ç«™: https://example.com');       if (!url) return;        // ç®€å•çš„URLéªŒè¯       if (!isValidUrl(url)) {         alert('è¯·è¾“å…¥æœ‰æ•ˆçš„URLåœ°å€ï¼');         return;       }        // æ ¹æ®URLè‡ªåŠ¨åˆ¤æ–­èµ„æºç±»å‹       const resourceType = getResourceTypeFromUrl(url);       addResource(resourceType, name, url, true);     }      // URLéªŒè¯å‡½æ•°     function isValidUrl(string) {       try 