 core.js æœªèƒ½æ­£ç¡®åŠ è½½     window.TaskPixel = window.TaskPixel || {};      // ç»Ÿä¸€çš„ç¼–è¾‘ä»»åŠ¡å‡½æ•°     function editTask(taskId) {       if (!taskId) {         alert('æœªæ‰¾åˆ°ä»»åŠ¡ID');         return;       }        let data, task;        try {         const raw = localStorage.getItem('taskpixel_data');         if (!raw) {           alert('æœªæ‰¾åˆ°ä»»åŠ¡æ•°æ®');           return;         }          data = JSON.parse(raw);         task = data.tasks.find(t => t.id === taskId);          if (!task) {           alert('æœªæ‰¾åˆ°æŒ‡å®šä»»åŠ¡');           return;         }        } catch (parseError) {         console.error('è¯»å–ä»»åŠ¡æ•°æ®é”™è¯¯:', parseError);         alert('è¯»å–ä»»åŠ¡æ•°æ®å¤±è´¥: ' + parseError.message);         return;       }        try {         // åˆ›å»ºç¼–è¾‘å¯¹è¯æ¡†         const dialogElement = document.createElement('div');         dialogElement.className = 'fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50';         dialogElement.id = 'edit-task-dialog';          // è®¡ç®—å½“å‰è¿›åº¦ï¼Œå†³å®šçŠ¶æ€é€‰é¡¹         let currentProgress = window.TaskPixel?.DataStoreAdapter?.getTaskProgress(taskId) || task.progress || 0;         let currentStatus = task.status || 'in-progress';         let isCompleted = currentProgress >= 100;          dialogElement.innerHTML = `           <div class="pixel-border bg-white p-6 w-full max-w-lg">             <h2 class="text-2xl font-display mb-6">ç¼–è¾‘ä»»åŠ¡</h2>             <form id="edit-task-form">               <div class="mb-4">                 <label class="block font-display text-lg mb-2" for="edit-task-title">ä»»åŠ¡æ ‡é¢˜</label>                 <input type="text" id="edit-task-title" class="w-full" required placeholder="è¾“å…¥ä»»åŠ¡æ ‡é¢˜" value="${task.title || ''}">               </div>               <div class="mb-4">                 <label class="block font-display text-lg mb-2" for="edit-task-description">ä»»åŠ¡æè¿°</label>                 <textarea id="edit-task-description" class="w-full h-32" placeholder="è¾“å…¥ä»»åŠ¡æè¿°">${task.description || ''}</textarea>               </div>               ${isCompleted ? `               <div class="mb-4">                 <label class="block font-display text-lg mb-2">çŠ¶æ€</label>                 <div class="w-full p-2 bg-green-100 text-green-800 border border-green-300 rounded">                   âœ… å·²å®Œæˆï¼ˆè¿›åº¦100%ï¼Œè‡ªåŠ¨è®¾ç½®ï¼‰                 </div>               </div>               ` : `               <div class="mb-4">                 <label class="block font-display text-lg mb-2" for="edit-task-status">çŠ¶æ€</label>                 <select id="edit-task-status" class="w-full">                   <option value="in-progress" ${currentStatus === 'in-progress' || currentStatus === 'todo' ? 'selected' : ''}>è¿›è¡Œä¸­</option>                   <option value="on-hold" ${currentStatus === 'on-hold' ? 'selected' : ''}>æç½®</option>                 </select>               </div>               `}               <div class="mb-4">                 <label class="block font-display text-lg mb-2">å½“å‰è¿›åº¦: ${currentProgress}%</label>                 <div class="w-full bg-gray-200 rounded-full h-2">                   <div class="bg-blue-600 h-2 rounded-full" style="width: ${currentProgress}%"></div>                 </div>               </div>               <div class="flex justify-end gap-4">                 <button type="button" id="cancel-edit-task" class="pixel-button">å–æ¶ˆ</button>                 <button type="submit" class="pixel-button bg-primary text-white">ä¿å­˜</button>               </div>             </form>           </div>         `;          document.body.appendChild(dialogElement);          // ç»‘å®šè¡¨å•æäº¤äº‹ä»¶         document.getElementById('edit-task-form').addEventListener('submit', function (e) {           e.preventDefault();            try {             const title = document.getElementById('edit-task-title').value.trim();             const description =