 = document.getElementById('edit-task-description').value.trim();              if (!title) {               alert('ä»»åŠ¡æ ‡é¢˜ä¸èƒ½ä¸ºç©º');               return;             }              // ç¡®å®šæœ€ç»ˆçŠ¶æ€             let finalStatus;             const progress = window.TaskPixel?.DataStoreAdapter?.getTaskProgress(taskId) || task.progress || 0;              if (progress >= 100) {               finalStatus = 'completed';             } else {               const statusSelect = document.getElementById('edit-task-status');               finalStatus = statusSelect ? statusSelect.value : 'in-progress';             }              // æ›´æ–°ä»»åŠ¡æ•°æ®             task.title = title;             task.description = description;             task.status = finalStatus;              // ä¿å­˜åˆ°localStorage             localStorage.setItem('taskpixel_data', JSON.stringify(data));              // æ˜¾ç¤ºæˆåŠŸæ¶ˆæ¯             alert('ä»»åŠ¡æ›´æ–°æˆåŠŸï¼');              // å…³é—­å¯¹è¯æ¡†             const dialogElement = document.getElementById('edit-task-dialog');             if (dialogElement) {               dialogElement.remove();             }              // åˆ·æ–°é¡µé¢             location.reload();            } catch (submitError) {             console.error('è¡¨å•æäº¤é”™è¯¯:', submitError);             alert('ä¿å­˜ä»»åŠ¡æ—¶å‡ºç°é”™è¯¯ï¼Œè¯·é‡è¯•ï¼š' + submitError.message);           }         });          // ç»‘å®šå–æ¶ˆæŒ‰é’®         document.getElementById('cancel-edit-task').addEventListener('click', function () {           const dialogElement = document.getElementById('edit-task-dialog');           if (dialogElement) {             dialogElement.remove();           }         });        } catch (dialogError) {         console.error('åˆ›å»ºç¼–è¾‘å¯¹è¯æ¡†é”™è¯¯:', dialogError);         alert('åˆ›å»ºç¼–è¾‘å¯¹è¯æ¡†å¤±è´¥: ' + dialogError.message);       }     }      // ç»Ÿä¸€çš„å®Œæˆä»»åŠ¡å‡½æ•°     function completeTask(taskId) {       if (!taskId) {         alert('æœªæ‰¾åˆ°ä»»åŠ¡ID');         return;       }        try {         const raw = localStorage.getItem('taskpixel_data');         if (!raw) {           alert('æœªæ‰¾åˆ°ä»»åŠ¡æ•°æ®');           return;         }          const data = JSON.parse(raw);         const task = data.tasks.find(t => t.id === taskId);          if (!task) {           alert('æœªæ‰¾åˆ°æŒ‡å®šä»»åŠ¡');           return;         }          // åˆ‡æ¢å®ŒæˆçŠ¶æ€         const oldStatus = task.status;         task.status = task.status === 'completed' ? 'in-progress' : 'completed';          // å¦‚æœæ ‡è®°ä¸ºå®Œæˆï¼Œè®¾ç½®è¿›åº¦ä¸º100%         if (task.status === 'completed') {           task.progress = 100;         }          // ä¿å­˜åˆ°localStorage         localStorage.setItem('taskpixel_data', JSON.stringify(data));          // æ˜¾ç¤ºæˆåŠŸæ¶ˆæ¯         const statusText = task.status === 'completed' ? 'å·²å®Œæˆ' : 'è¿›è¡Œä¸­';         alert(`ä»»åŠ¡çŠ¶æ€å·²æ›´æ–°ä¸ºï¼š${statusText}`);          // åˆ·æ–°é¡µé¢         location.reload();        } catch (e) {         console.error('æ›´æ–°ä»»åŠ¡çŠ¶æ€é”™è¯¯:', e);         alert('æ›´æ–°ä»»åŠ¡çŠ¶æ€æ—¶å‡ºé”™: ' + e.message);       }     }      // æ·»åŠ å·¥ä½œè¿›åº¦è®°å½•     function addWorkProgress(taskId, content, hours) {       if (!taskId) {         alert('æœªæ‰¾åˆ°ä»»åŠ¡ID');         return;       }        try {         const raw = localStorage.getItem('taskpixel_data');         if (!raw) {           alert('æœªæ‰¾åˆ°ä»»åŠ¡æ•°æ®');           return;         }          const data = JSON.parse(raw);         const task = data.tasks.find(t => t.id === taskId);          if (!task) {           alert('æœªæ‰¾åˆ°æŒ‡å®šä»»åŠ¡');           return;         }          // åˆå§‹åŒ–timelineæ•°ç»„         if (!task.timeline) {           task.timeline = [];         }          // åˆ›å»ºæ–°çš„è¿›åº¦è®°å½•         const progressEntry = {           id: 'progress-' + Date.now(),           date: new Date().toISOString(),           content: content,           hours: hours,           timestamp: Date.now()         };          task.timeline.push(progressEntry);          // æ›´æ–°æ€»å·¥ä½œæ—¶é—´         const totalHours = task.timeline.reduce((sum, entry) => sum + (entry.hours || 0), 0);         task.totalHours = totalHours;          // ä¿å­˜åˆ°localStorage         localStorage.setItem('taskpixel_data', JSON.stringify(data));          // æ¸…ç©ºè¡¨å•         document.getElementById('work-content').value = '';         document.getElementById('time-spent').value = '';          // é‡æ–°æ¸²æŸ“è¿›åº¦å†å²         renderProgressHistory(task.timeline);          alert('å·¥ä½œè¿›åº¦è®°å½•æˆåŠŸï¼');        } catch (e) {         alert('è®°å½•å·¥ä½œè¿›åº¦æ—¶å‡ºé”™: ' + e.message);       }     }      // æ¸²æŸ“è¿›åº¦å†å²     function renderProgressHistory(timeline) {       const container = document.querySelector('.timeline-container');       if (!container) return;        container.innerHTML = '';        if (!timeline || timeline.length === 0) {         container.innerHTML = `           <div class="text-center py-8">             <p class="text-gray-500 mb-4">è¿˜æ²¡æœ‰å·¥ä½œè¿›åº¦è®°å½•</p>             <p class="text-gray-400 text-sm">å¼€å§‹è®°å½•æ‚¨çš„å·¥ä½œè¿›åº¦å§ï¼</p>           </div>         `;         return;       }        // æŒ‰æ—¶é—´å€’åºæ’åˆ—       const sortedTimeline = [...timeline].sort((a, b) => b.timestamp - a.timestamp);        sortedTimeline.forEach(entry => {         const entryElement = document.createElement('div');         entryElement.className = 'pixel-border bg-white p-4 mb-4 hover:shadow-lg transition-shadow';          const date = new Date(entry.date);         const formattedDate = date.toLocaleDateString('zh-CN') + ' ' + date.toLocaleTimeString('zh-CN', { hour: '2-digit', minute: '2-digit' });          entryElement.innerHTML = `           <div class="flex justify-between items-start mb-3">             <div class="flex items-center gap-2">               <div class="w-3 h-3 bg-primary"></div>               <span class="font-display text-sm text-gray-600">${formattedDate}</span>             </div>             <span class="pixel-button bg-primary text-white px-3 py-1 text-xs">${entry.hours} å°æ—¶</span>           </div>           <div class="pl-5">             <p class="font-display text-lg text-text-primary">${entry.content}</p>           </div>         `;          container.appendChild(entryElement);       });     }   </script>   <script src="js/core.js"></script>   <script src="js/dataStoreAdapter.js"></script>   <script src="js/tagManager.js"></script>   <script src="js/aiAssist.js"></script>   <script src="js/taskDetail.js"></script>   <script>     // ç®€åŒ–çš„åˆå§‹åŒ–è„šæœ¬ - ç§»é™¤æ‰€æœ‰è°ƒè¯•è¾“å‡º     document.addEventListener('DOMContentLoaded', function () {       // åŸºç¡€æŒ‰é’®äº‹ä»¶       document.querySelector('.back-button')?.addEventListener('click', function () {         window.location.href = 'index.html';       });        document.querySelector('.edit-button')?.addEventListener('click', function () {         const taskId = new URLSearchParams(window.location.search).get('id') || window.currentTaskId;         editTask(taskId);       });        document.querySelector('.complete-button')?.addEventListener('click', function () {         const taskId = new URLSearchParams(window.location.search).get('id');         completeTask(taskId);       });        // è®°å½•å·¥ä½œè¿›åº¦è¡¨å•å¤„ç†       document.getElementById('timeline-form')?.addEventListener('submit', function (e) {         e.preventDefault();         const taskId = new URLSearchParams(window.location.search).get('id');         const workContent = document.getElementById('work-content').value.trim();         const timeSpent = parseFloat(document.getElementById('time-spent').value);          if (!workContent) {           alert('è¯·è¾“å…¥å·¥ä½œå†…å®¹');           return;         }         if (!timeSpent || timeSpent <= 0) {           alert('è¯·è¾“å…¥æœ‰æ•ˆçš„å·¥ä½œæ—¶é—´');           return;         }          addWorkProgress(taskId, workContent, timeSpent);       });        // åˆå§‹åŒ– TaskPixel       try {         if (typeof TaskPixel?.init === 'function') {           TaskPixel.init();         }       } catch (e) {         // é™é»˜å¤„ç†é”™è¯¯       }        // åˆå§‹åŒ– TaskDetail       try {         const taskId = new URLSearchParams(window.location.search).get('id');         if (taskId && window.TaskPixel?.TaskDetail?.init) {           window.currentTaskId = taskId;           TaskPixel.TaskDetail.currentTaskId = taskId;           TaskPixel.TaskDetail.init();         }       } catch (e) {         // é™é»˜å¤„ç†é”™è¯¯       }        // å†…è”æ ‡ç­¾ç®¡ç†å™¨å°†åœ¨å®šä¹‰ååˆå§‹åŒ–        // å¤‡ç”¨æ•°æ®æ˜¾ç¤ºé€»è¾‘ - ç¡®ä¿ä»»åŠ¡ä¿¡æ¯èƒ½å¤Ÿæ˜¾ç¤º       setTimeout(function () {         const taskId = new URLSearchParams(window.location.search).get('id');         if (taskId) {           try {             const raw = localStorage.getItem('taskpixel_data');             if (raw) {               const data = JSON.parse(raw);               const task = (data.tasks || []).find(t => t.id === taskId);                if (task) {                 // æ›´æ–°æ ‡é¢˜                 const titleEl = document.querySelector('.task-title');                 if (titleEl && !titleEl.textContent.includes(task.title)) {                   titleEl.textContent = 'ä»»åŠ¡: ' + task.title;                 }                  // è®¾ç½®ä»»åŠ¡æ ‡ç­¾å®¹å™¨çš„ task-id                 const taskTagContainer = document.querySelector('[data-target="task"]');                 if (taskTagContainer) {                   taskTagContainer.dataset.taskId = taskId;                 }                  // æ¸²æŸ“ä»»åŠ¡æ ‡ç­¾ï¼ˆä»…åœ¨å®¹å™¨ä¸ºç©ºæ—¶ï¼‰                 const taskTagsDisplay = document.querySelector('.task-tags-display');                 if (typeof InlineTagManager !== 'undefined' && InlineTagManager.renderTaskTags &&                   taskTagsDisplay && taskTagsDisplay.children.length === 0) {                   InlineTagManager.renderTaskTags(taskId);                 }                  // æ›´æ–°æè¿°                 const descEl = document.querySelector('.task-description');                 if (descEl && !descEl.textContent) {                   descEl.textContent = task.description || 'æš‚æ— æè¿°';                 }                  // æ›´æ–°è¿›åº¦ - ä½¿ç”¨ç»Ÿä¸€çš„ updateProgressBar å‡½æ•°                 updateProgressBar(taskId);                  // æ›´æ–°çŠ¶æ€                 const statusEl = document.querySelector('.task-status');                 if (statusEl) {                   let statusText = 'è¿›è¡Œä¸­';                   let statusClass = 'bg-accent-yellow/20 text-accent-yellow-800 border-accent-yellow-800';                    switch (task.status) {                     case 'todo':                       statusText = 'å¾…å¤„ç†';                       statusClass = 'bg-accent-blue/20 text-accent-blue-800 border-accent-blue-800';                       break;                     case 'on-hold':                       statusText = 'æç½®';                       statusClass = 'bg-accent-red/20 text-accent-red-800 border-accent-red-800';                       break;                     case 'completed':                       statusText = 'å·²å®Œæˆ';                       statusClass = 'bg-accent-green/20 text-accent-green-800 border-accent-green-800';                       break;                     case 'in-progress':                     default:                       statusText = 'è¿›è¡Œä¸­';                       statusClass = 'bg-accent-yellow/20 text-accent-yellow-800 border-accent-yellow-800';                       break;                   }                   statusEl.textContent = statusText;                    // æ›´æ–°çŠ¶æ€æ ·å¼                   statusEl.className = `font-display text-sm px-2 py-1 task-status border-2 ${statusClass}`;                 }                  // æ¸²æŸ“ç›®æ ‡                 const goalsContainer = document.querySelector('.goals-container');                 if (goalsContainer) {                   renderGoals(task.goals || [], taskId);                   // æ¸²æŸ“ç›®æ ‡åæ›´æ–°è¿›åº¦æ¡                   updateProgressBar(taskId);                 }                  // æ¸²æŸ“è¿›åº¦å†å²                 if (task.timeline) {                   renderProgressHistory(task.timeline);                 }               }             }           } catch (e) {             // é™é»˜å¤„ç†é”™è¯¯           }         }       }, 1000);        // ç›®æ ‡æ¸²æŸ“å‡½æ•°       function renderGoals(goals, taskId) {         const goalsContainer = document.querySelector('.goals-container');         if (!goalsContainer) return;          // æ¸…ç©ºå®¹å™¨         goalsContainer.innerHTML = '';          // æ·»åŠ æ ‡é¢˜å’ŒAIåŠ©æ‰‹æŒ‰é’®         const header = document.createElement('div');         header.className = 'flex items-center justify-between mb-6';         header.innerHTML = `           <h3 class="font-display text-2xl text-text-primary">ç›®æ ‡ä¸å­æ­¥éª¤</h3>           <button class="pixel-button bg-purple-600 text-white px-4 py-2 text-sm">             <span class="mr-2">âœ¨</span>AI åŠ©æ‰‹           </button>         `;         goalsContainer.appendChild(header);          // æ¸²æŸ“ç›®æ ‡åˆ—è¡¨         if (goals && goals.length > 0) {           goals.forEach(goal => {             const goalCard = createGoalCard(goal, taskId);             goalsContainer.appendChild(goalCard);              // æ¸²æŸ“ç›®æ ‡æ ‡ç­¾ï¼ˆä»…åœ¨å®¹å™¨ä¸ºç©ºæ—¶ï¼‰             const goalTagsDisplay = goalCard.querySelector('.goal-tags-display');             if (typeof InlineTagManager !== 'undefined' && InlineTagManager.renderGoalTags &&               goalTagsDisplay && goalTagsDisplay.children.length === 0) {               InlineTagManager.renderGoalTags(goal.id);             }           });            // æ·»åŠ "æ·»åŠ æ–°ç›®æ ‡"æŒ‰é’®           const addButton = document.createElement('div');           addButton.className = 'text-center mt-6';           addButton.innerHTML = `             <button class="pixel-button bg-primary text-white px-4 py-2 add-goal-btn" title="æ·»åŠ æ–°ç›®æ ‡">æ·»åŠ ç›®æ ‡</button>           `;           goalsContainer.appendChild(addButton);         } else {           // ç©ºçŠ¶æ€           const emptyState = document.createElement('div');           emptyState.className = 'text-center py-8';           emptyState.innerHTML = `             <p class="text-gray-500 mb-4">è¿˜æ²¡æœ‰è®¾ç½®ç›®æ ‡</p>             <button class="pixel-button bg-primary text-white px-4 py-2 add-goal-btn" title="æ·»åŠ ç›®æ ‡">æ·»åŠ ç›®æ ‡</button>           `;           goalsContainer.appendChild(emptyState);         }          // ç»‘å®šæ·»åŠ ç›®æ ‡æŒ‰é’®         bindAddGoalEvents(taskId);          // åˆå§‹åŒ–ç›®æ ‡æ‹–åŠ¨æ’åº         initializeGoalSorting(taskId);          // åº”ç”¨å·²ä¿å­˜çš„æŠ˜å é¦–é€‰é¡¹ï¼ˆå¦‚æœæœ‰ï¼‰ï¼Œå¹¶åˆå§‹åŒ–å­æ­¥éª¤çš„ Sortable å®ä¾‹         try {           const raw = localStorage.getItem('taskpixel_ui');           const prefs = raw ? JSON.parse(raw) : {};           const collapsed = (prefs && prefs.collapsedGoals) ? prefs.collapsedGoals[taskId] || {} : {};           document.querySelectorAll('.substeps-wrapper').forEach(wrapper => {             const gid = wrapper.dataset.goalId;             const container = wrapper.querySelector('.substeps-container');             if (!container) return;             const isCollapsed = !!collapsed[gid];             container.setAttribute('data-collapsed', isCollapsed ? 'true' : 'false');             container.style.display = isCollapsed ? 'none' : '';             const toggle = wrapper.querySelector('.toggle-substeps-btn');             if (toggle) toggle.setAttribute('aria-expanded', isCollapsed ? 'false' : 'true');           });            // åˆå§‹åŒ–æ¨¡å—åŒ– TaskDetail çš„å­æ­¥éª¤ sortableï¼ˆå¦‚æœå­˜åœ¨ï¼‰           if (window.TaskPixel && TaskPixel.TaskDetail && typeof TaskPixel.TaskDetail.attachSubstepsSortables === 'function') {             TaskPixel.TaskDetail.attachSubstepsSortables();           }         } catch (e) {           console.error('åº”ç”¨æŠ˜å é¦–é€‰é¡¹/åˆå§‹åŒ–å­æ­¥éª¤ sortable æ—¶å‡ºé”™', e);         }       }        // åˆå§‹åŒ–å­æ­¥éª¤æ‹–åŠ¨æ’åº       function initializeSubstepSorting(goalCard, goalId, taskId) {         const substepsContainer = goalCard.querySelector('.substeps-container');         if (!substepsContainer || !window.Sortable) return;          // åˆ›å»ºSortableå®ä¾‹         new Sortable(substepsContainer, {           handle: '.drag-handle',           animation: 150,           ghostClass: 'sortable-ghost',           chosenClass: 'sortable-chosen',           dragClass: 'sortable-drag',           onEnd: function (evt) {             // è·å–æ–°çš„æ’åº             const substepItems = Array.from(substepsContainer.querySelectorAll('.substep-item'));             const newOrder = substepItems.map(item => item.dataset.substepId);              // æ›´æ–°æ•°æ®å­˜å‚¨             updateSubstepOrder(taskId, goalId, newOrder);           }         });       }        // æ›´æ–°å­æ­¥éª¤æ’åº       function updateSubstepOrder(taskId, goalId, newOrder) {         try {           // ä¼˜å…ˆä½¿ç”¨ DataStore API           if (window.TaskPixel && TaskPixel.DataStore && typeof TaskPixel.DataStore.updateSubstepOrder === 'function') {             TaskPixel.DataStore.updateSubstepOrder(taskId, goalId, newOrder);             return;           }         } catch (e) {           console.error('æ›´æ–°å­æ­¥éª¤æ’åºå¤±è´¥ via DataStore', e);         }          // å¤‡ç”¨æ–¹æ¡ˆï¼šç›´æ¥æ“ä½œlocalStorage         try {           const raw = localStorage.getItem('taskpixel_data');           if (!raw) return;            const data = JSON.parse(raw);           const task = data.tasks.find(t => t.id === taskId);           if (!task || !task.goals) return;            const goal = task.goals.find(g => g.id === goalId);           if (!goal || !goal.substeps) return;            // é‡æ–°æ’åºå­æ­¥éª¤           const reorderedSubsteps = [];           newOrder.forEach((substepId, index) => {             const substep = goal.substeps.find(s => s.id === substepId);             if (substep) {               substep.order = index;               reorderedSubsteps.push(substep);             }           });            goal.substeps = reorderedSubsteps;           localStorage.setItem('taskpixel_data', JSON.stringify(data));            console.log('å­æ­¥éª¤æ’åºå·²æ›´æ–°');         } catch (e) {           console.error('æ›´æ–°å­æ­¥éª¤æ’åºå¤±è´¥', e);         }       }        // åˆå§‹åŒ–ç›®æ ‡æ‹–åŠ¨æ’åº       function initializeGoalSorting(taskId) {         const goalsContainer = document.querySelector('.goals-container');         if (!goalsContainer || !window.Sortable) return;          // åˆ›å»ºSortableå®ä¾‹         new Sortable(goalsContainer, {           handle: '.drag-handle',           animation: 150,           ghostClass: 'sortable-ghost',           chosenClass: 'sortable-chosen',           dragClass: 'sortable-drag',           filter: '.text-center', // æ’é™¤"æ·»åŠ æ–°ç›®æ ‡"æŒ‰é’®åŒºåŸŸ           onEnd: function (evt) {             // è·å–æ‰€æœ‰ç›®æ ‡å¡ç‰‡çš„æ–°æ’åº             const goalCards = Array.from(goalsContainer.querySelectorAll('.goal-card'));             const newOrder = goalCards.map(card => card.dataset.goalId);              // æ›´æ–°æ•°æ®å­˜å‚¨             updateGoalOrder(taskId, newOrder);           }         });       }        // æ›´æ–°ç›®æ ‡æ’åº       function updateGoalOrder(taskId, newOrder) {         try {           // ä¼˜å…ˆä½¿ç”¨ DataStore API           if (window.TaskPixel && TaskPixel.DataStore && typeof TaskPixel.DataStore.updateGoalOrder === 'function') {             TaskPixel.DataStore.updateGoalOrder(taskId, newOrder);             return;           }         } catch (e) {           console.error('æ›´æ–°ç›®æ ‡æ’åºå¤±è´¥ via DataStore', e);         }          // å¤‡ç”¨æ–¹æ¡ˆï¼šç›´æ¥æ“ä½œlocalStorage         try {           const raw = localStorage.getItem('taskpixel_data');           if (!raw) return;            const data = JSON.parse(raw);           const task = data.tasks.find(t => t.id === taskId);           if (!task || !task.goals) return;            // é‡æ–°æ’åºç›®æ ‡           const reorderedGoals = [];           newOrder.forEach((goalId, index) => {             const goal = task.goals.find(g => g.id === goalId);             if (goal) {               goal.order = index;               reorderedGoals.push(goal);             }           });            task.goals = reorderedGoals;           localStorage.setItem('taskpixel_data', JSON.stringify(data));            console.log('ç›®æ ‡æ’åºå·²æ›´æ–°');         } catch (e) {           console.error('æ›´æ–°ç›®æ ‡æ’åºå¤±è´¥', e);         }       }        // åˆ›å»ºç›®æ ‡å¡ç‰‡       function createGoalCard(goal, taskId) {         const card = document.createElement('div');         card.className = 'border-4 border-black bg-white mb-6 p-4 goal-card';         card.dataset.goalId = goal.id;          // è®¡ç®—è¿›åº¦         const total = goal.substeps ? goal.substeps.length : 0;         const completed = goal.substeps ? goal.substeps.filter(s => s.completed).length : 0;          card.innerHTML = `           <div class="flex items-start justify-between mb-4">               <div class="drag-handle cursor-move text-gray-400 hover:text-gray-600 mr-3 mt-1" title="æ‹–æ‹½æ’åºç›®æ ‡">                 <svg width="14" height="14" viewBox="0 0 12 12" fill="currentColor">                   <circle cx="3" cy="3" r="1"/>                   <circle cx="9" cy="3" r="1"/>                   <circle cx="3" cy="6" r="1"/>                   <circle cx="9" cy="6" r="1"/>                   <circle cx="3" cy="9" r="1"/>                   <circle cx="9" cy="9" r="1"/>                 </svg>               </div>               <div class="flex-1">               <div class="flex items-center justify-between">                 <h4 class="font-display text-lg font-bold mb-2">${goal.title || 'æœªå‘½åç›®æ ‡'}</h4>                 <button class="toggle-substeps-btn pixel-toggle-btn" data-goal-id="${goal.id}" title="å±•å¼€/æŠ˜å å­æ­¥éª¤" aria-expanded="false"></button>               </div>               <p class="text-gray-600 text-sm mb-2">${goal.description || ''}</p>             </div>             <div class="flex gap-2">         <button class="action-btn bg-green-500 edit-goal-btn"                        data-goal-id="${goal.id}" title="ä¿®æ”¹ç›®æ ‡">ç¼–è¾‘</button>         <button class="action-btn bg-blue-500 add-substep-btn"                        data-goal-id="${goal.id}" title="æ·»åŠ å­æ­¥éª¤">æ–°å¢</button>         <button class="action-btn bg-red-500 delete-goal-btn"                        data-goal-id="${goal.id}" title="åˆ é™¤ç›®æ ‡">åˆ é™¤</button>             </div>           </div>            <!-- ç›®æ ‡æ ‡ç­¾åŒºåŸŸï¼ˆæ ‡ç­¾æ˜¾ç¤ºåŒºä¸ +Tags æŒ‰é’®åŒè¡Œï¼Œçª„å±å›é€€ï¼‰ -->           <div class="goal-tags-section mt-2 mb-3">             <div class="flex items-center gap-2 flex-wrap">               <div class="goal-tags-display flex flex-wrap gap-1">                 <!-- ç›®æ ‡æ ‡ç­¾æ˜¾ç¤ºåŒºåŸŸ -->               </div>               <div class="tags-input-container" data-target="goal" data-goal-id="${goal.id}" data-task-id="${taskId}">                 <button class="add-tags-btn">+Tags</button>                 <div class="tag-input-wrapper hidden">                   <input type="text" class="tag-input-field" placeholder="è¾“å…¥æ ‡ç­¾..." autocomplete="off">                   <div class="tag-suggestions hidden"></div>                 </div>               </div>             </div>           </div>                      <div class="substeps-wrapper" data-goal-id="${goal.id}">             <div class="substeps-container" role="region" aria-label="å­æ­¥éª¤åˆ—è¡¨" data-collapsed="false">             ${goal.substeps ? goal.substeps.map(substep => `               <div class="flex items-center gap-3 py-2 px-3 hover:bg-gray-50 substep-item" data-substep-id="${substep.id}">                 <div class="drag-handle cursor-move text-gray-400 hover:text-gray-600 mr-2" title="æ‹–æ‹½æ’åº">                   <svg width="12" height="12" viewBox="0 0 12 12" fill="currentColor">                     <circle cx="3" cy="3" r="1"/>                     <circle cx="9" cy="3" r="1"/>                     <circle cx="3" cy="6" r="1"/>                     <circle cx="9" cy="6" r="1"/>                     <circle cx="3" cy="9" r="1"/>                     <circle cx="9" cy="9" r="1"/>                   </svg>                 </div>                 <input type="checkbox" ${substep.completed ? 'checked' : ''}                         class="substep-checkbox w-4 h-4"                         data-goal-id="${goal.id}"                         data-substep-id="${substep.id}">                 <span class="flex-1 ${substep.completed ? 'line-through text-gray-500' : ''}">${substep.content || substep.title}</span>                 <div class="flex gap-1 items-center">                   <button class="action-btn bg-green-500 edit-substep-btn"                                data-goal-id="${goal.id}"                                data-substep-id="${substep.id}" title="ä¿®æ”¹å­æ­¥éª¤">ç¼–è¾‘</button>                   <button class="action-btn bg-red-500 delete-substep-btn"                                data-goal-id="${goal.id}"                                data-substep-id="${substep.id}" title="åˆ é™¤å­æ­¥éª¤">åˆ é™¤</button>                 </div>               </div>             `).join('') : ''}             </div>           </div>         `;          // ç»‘å®šäº‹ä»¶         bindGoalCardEvents(card, goal, taskId);          return card;       }        // ç»‘å®šç›®æ ‡å¡ç‰‡äº‹ä»¶       function bindGoalCardEvents(card, goal, taskId) {         // æ·»åŠ å­æ­¥éª¤         const addBtn = card.querySelector('.add-substep-btn');         if (addBtn) {           addBtn.addEventListener('click', () => openAddSubstepDialog(goal.id, taskId));         }          // ä¿®æ”¹ç›®æ ‡         const editBtn = card.querySelector('.edit-goal-btn');         if (editBtn) {           editBtn.addEventListener('click', () => openEditGoalDialog(goal.id, taskId));         }          // åˆ é™¤ç›®æ ‡         const deleteBtn = card.querySelector('.delete-goal-btn');         if (deleteBtn) {           deleteBtn.addEventListener('click', () => {             if (confirm('ç¡®å®šè¦åˆ é™¤è¿™ä¸ªç›®æ ‡å—ï¼Ÿ')) {               deleteGoal(taskId, goal.id);             }           });         }          // å¤é€‰æ¡†äº‹ä»¶         const checkboxes = card.querySelectorAll('.substep-checkbox');         checkboxes.forEach(checkbox => {           checkbox.addEventListener('change', function () {             updateSubstepStatus(               taskId,               this.dataset.goalId,               this.dataset.substepId,               this.checked             );           });         });          // ä¿®æ”¹å­æ­¥éª¤æŒ‰é’®         const editSubstepButtons = card.querySelectorAll('.edit-substep-btn');         editSubstepButtons.forEach(btn => {           btn.addEventListener('click', () => {             openEditSubstepDialog(taskId, btn.dataset.goalId, btn.dataset.substepId);           });         });          // åˆ é™¤å­æ­¥éª¤æŒ‰é’®         const deleteButtons = card.querySelectorAll('.delete-substep-btn');         deleteButtons.forEach(btn => {           btn.addEventListener('click', () => {             if (confirm('ç¡®å®šè¦åˆ é™¤è¿™ä¸ªå­æ­¥éª¤å—ï¼Ÿ')) {               deleteSubstep(taskId, btn.dataset.goalId, btn.dataset.substepId);             }           });         });            // åˆå§‹åŒ–å­æ­¥éª¤æ‹–åŠ¨æ’åº         initializeSubstepSorting(card, goal.id, taskId);          // æŠ˜å /å±•å¼€å­æ­¥éª¤åŠŸèƒ½         try {           const toggleBtn = card.querySelector('.toggle-substeps-btn');           const wrapper = card.querySelector('.substeps-wrapper');           const container = wrapper && wrapper.querySelector('.substeps-container');            // helper: è¯»å–/å†™å…¥ UI prefs           function getUIPrefs() {             try {               const raw = localStorage.getItem('taskpixel_ui');               return raw ? JSON.parse(raw) : {};             } catch (e) { return {}; }           }           function setUIPrefs(prefs) {             try { localStorage.setItem('taskpixel_ui', JSON.stringify(prefs)); } catch (e) { }           }            if (toggleBtn && container) {             // åˆå§‹åŒ–æŠ˜å çŠ¶æ€ï¼ˆä¼˜å…ˆä» taskpixel_ui ä¸­è¯»å–ï¼‰             const prefs = getUIPrefs();             prefs.collapsedGoals = prefs.collapsedGoals || {};             prefs.collapsedGoals[taskId] = prefs.collapsedGoals[taskId] || {};             const collapsed = !!prefs.collapsedGoals[taskId][goal.id];             container.setAttribute('data-collapsed', collapsed ? 'true' : 'false');             if (collapsed) container.style.display = 'none';             // è®¾ç½®åˆå§‹çŠ¶æ€             toggleBtn.setAttribute('aria-expanded', collapsed ? 'false' : 'true');              toggleBtn.addEventListener('click', () => {               const isCollapsed = container.getAttribute('data-collapsed') === 'true';               if (isCollapsed) {                 container.setAttribute('data-collapsed', 'false');                 container.style.display = '';                 prefs.collapsedGoals[taskId][goal.id] = false;               } else {                 container.setAttribute('data-collapsed', 'true');                 container.style.display = 'none';                 prefs.collapsedGoals[taskId][goal.id] = true;               }               // æ›´æ–° aria-expanded å±æ€§ï¼ŒCSS ä¼šè‡ªåŠ¨æ›´æ–°ç®­å¤´æ–¹å‘               toggleBtn.setAttribute('aria-expanded', isCollapsed ? 'true' : 'false');               setUIPrefs(prefs);             });           }         } catch (e) {           console.error('åˆå§‹åŒ–æŠ˜å å­æ­¥éª¤å¤±è´¥', e);         }       }            // ç»‘å®šæ·»åŠ ç›®æ ‡äº‹ä»¶       function bindAddGoalEvents(taskId) {         const addBtns = document.querySelectorAll('.add-goal-btn');         addBtns.forEach(btn => {           btn.addEventListener('click', () => openAddGoalDialog(taskId));         });       }        // æ‰“å¼€æ·»åŠ ç›®æ ‡å¯¹è¯æ¡†       function openAddGoalDialog(taskId) {         const dialog = document.createElement('div');         dialog.className = 'fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50';         dialog.id = 'add-goal-dialog';          dialog.innerHTML = `           <div class="pixel-border bg-white p-6 w-full max-w-lg">             <h2 class="text-2xl font-display mb-6">æ·»åŠ ç›®æ ‡</h2>             <form id="add-goal-form">               <div class="mb-4">                 <label class="block font-display text-lg mb-2" for="goal-title">ç›®æ ‡æ ‡é¢˜ *</label>                 <input type="text" id="goal-title" class="w-full" required placeholder="è¾“å…¥ç›®æ ‡æ ‡é¢˜">               </div>               <div class="mb-4">                 <label class="block font-display text-lg mb-2" for="goal-description">ç›®æ ‡æè¿°</label>                 <textarea id="goal-description" class="w-full h-32" placeholder="è¾“å…¥ç›®æ ‡æè¿°ï¼ˆå¯é€‰ï¼‰"></textarea>               </div>               <div class="flex justify-end gap-4">                 <button type="button" id="cancel-add-goal" class="pixel-button">å–æ¶ˆ</button>                 <button type="submit" class="pixel-button bg-primary text-white">æ·»åŠ </button>               </div>             </form>           </div>         `;          document.body.appendChild(dialog);          // ç»‘å®šäº‹ä»¶         document.getElementById('add-goal-form').addEventListener('submit', function (e) {           e.preventDefault();           const title = document.getElementById('goal-title').value.trim();           const description = document.getElementById('goal-description').value.trim();            if (!title) {             alert('è¯·è¾“å…¥ç›®æ ‡æ ‡é¢˜');             return;           }            addGoal(taskId, title, description);           dialog.remove();         });          document.getElementById('cancel-add-goal').addEventListener('click', () => {           dialog.remove();         });          // èšç„¦åˆ°æ ‡é¢˜è¾“å…¥æ¡†         setTimeout(() => {           document.getElementById('goal-title').focus();         }, 100);       }        // æ·»åŠ ç›®æ ‡       function addGoal(taskId, title, description) {         try {           const raw = localStorage.getItem('taskpixel_data');           if (raw) {             const data = JSON.parse(raw);             const task = data.tasks.find(t => t.id === taskId);             if (task) {               if (!task.goals) task.goals = [];                const newGoal = {                 id: 'goal-' + Date.now(),                 title: title,                 description: description || '',                 substeps: []               };                task.goals.push(newGoal);               localStorage.setItem('taskpixel_data', JSON.stringify(data));                // é‡æ–°æ¸²æŸ“               renderGoals(task.goals, taskId);             }           }         } catch (e) {           alert('æ·»åŠ ç›®æ ‡å¤±è´¥: ' + e.message);         }       }        // æ‰“å¼€ä¿®æ”¹ç›®æ ‡å¯¹è¯æ¡†       function openEditGoalDialog(goalId, taskId) {         try {           const raw = localStorage.getItem('taskpixel_data');           if (!raw) return;            const data = JSON.parse(raw);           const task = data.tasks.find(t => t.id === taskId);           if (!task || !task.goals) return;            const goal = task.goals.find(g => g.id === goalId);           if (!goal) return;            const dialog = document.createElement('div');           dialog.className = 'fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50';           dialog.id = 'edit-goal-dialog';            dialog.innerHTML = `             <div class="pixel-border bg-white p-6 w-full max-w-lg">               <h2 class="text-2xl font-display mb-6">ä¿®æ”¹ç›®æ ‡</h2>               <form id="edit-goal-form">                 <div class="mb-4"> 