urn;         }          setTimeout(() => {           try {             // è·å–å®é™…è¿›åº¦             let actualProgress = 0;              if (window.TaskPixel?.DataStoreAdapter?.getTaskProgress) {               actualProgress = window.TaskPixel.DataStoreAdapter.getTaskProgress(taskId);             } else {               // å¤‡ç”¨è®¡ç®—æ–¹æ³•               const raw = localStorage.getItem('taskpixel_data');               if (raw) {                 const data = JSON.parse(raw);                 const task = data.tasks?.find(t => t.id === taskId);                 if (task) {                   // è®¡ç®—åŸºäºç›®æ ‡å’Œå­æ­¥éª¤çš„è¿›åº¦                   let totalSubsteps = 0;                   let completedSubsteps = 0;                    if (task.goals && Array.isArray(task.goals)) {                     task.goals.forEach(goal => {                       if (goal.substeps && Array.isArray(goal.substeps)) {                         goal.substeps.forEach(substep => {                           totalSubsteps++;                           if (substep.completed) {                             completedSubsteps++;                           }                         });                       }                     });                   }                    actualProgress = totalSubsteps > 0 ? Math.round((completedSubsteps / totalSubsteps) * 100) : 0;                 }               }             }              // æ›´æ–°è¿›åº¦æ¡             const progressEl = document.querySelector('.progress-bar-fill');             const progressTextEl = document.querySelector('.progress-text');              if (progressEl) {               progressEl.style.width = actualProgress + '%';             }             if (progressTextEl) {               progressTextEl.textContent = actualProgress + '% å®Œæˆ';             }             // æ›´æ–°å†…éƒ¨æ ‡ç­¾ï¼ˆè‹¥å­˜åœ¨ï¼‰             const progressLabelEl = document.querySelector('.progress-label');             if (progressLabelEl) {               progressLa