    }         } catch (e) {           console.error('åº”ç”¨æŠ˜å é¦–é€‰é¡¹/åˆå§‹åŒ–å­æ­¥éª¤ sortable æ—¶å‡ºé”™', e);         }       }        // åˆå§‹åŒ–å­æ­¥éª¤æ‹–åŠ¨æ’åº       function initializeSubstepSorting(goalCard, goalId, taskId) {         const substepsContainer = goalCard.querySelector('.substeps-container');         if (!substepsContainer || !window.Sortable) return;          // åˆ›å»ºSortableå®ä¾‹         new Sortable(substepsContainer, {           handle: '.drag-handle',           animation: 150,           ghostClass: 'sortable-ghost',           chosenClass: 'sortable-chosen',           dragClass: 'sortable-drag',           onEnd: function (evt) {             // è·å–æ–°çš„æ’åº             const substepItems = Array.from(substepsContainer.querySelectorAll('.substep-item'));             const newOrder = substepItems.map(item => item.dataset.substepId);              // æ›´æ–°æ•°æ®å­˜å‚¨             updateSubstepOrder(taskId, goalId, newOrder);           }         });       }        // æ›´æ–°å­æ­¥éª¤æ’åº       function updateSubstepOrder(taskId, goalId, newOrder) {         try {           // ä¼˜å…ˆä½¿ç”¨ DataStore API           if (window.TaskPixel && TaskPixel.DataStore && typeof TaskPixel.DataStore.updateSubstepOrder === 'function') {             TaskPixel.DataStore.updateSubstepOrder(taskId, goalId, newOrder);             return;           }         } catch (e) {           console.error('æ›´æ–°å­æ­¥éª¤æ’åºå¤±è´¥ via DataStore', e);         }          // å¤‡ç”¨æ–¹æ¡ˆï¼šç›´æ¥æ“ä½œlocalStorage         try {           const raw = localStorage.getItem('taskpixel_data');           if (!raw) return;            const data = JSON.parse(raw);           const task = data.tasks.find(t => t.id === taskId);           if (!task || !task.goals) return;            const goal = task.goals.find(g => g.id === goalId);           if (!goal || !goal.substeps) return;            // é‡æ–°æ’åºå­æ­¥éª¤           const reorderedSubsteps = [];           newOrder.forEach((substepId, index) => {             const substep = goal.substeps.find(s => s.id === substepId);             if (substep) {               substep.order = index;               reorderedSubsteps.push(substep);             }           });            goal.substeps = reorderedSubsteps;           localStorage.setItem('taskpixel_data', JSON.stringify(data));            console.log('å­æ­¥éª¤æ’åºå·²æ›´æ–°');         } catch (e) {           console.error('æ›´æ–°å­æ­¥éª¤æ’åºå¤±è´¥', e);         }       }        // åˆå§‹åŒ–ç›®æ ‡æ‹–åŠ¨æ’åº       function initializeGoalSorting(taskId) {         const goalsContainer = document.querySelector('.goals-container');         if (!goalsContainer || !window.Sortable) return;          // åˆ›å»ºSortableå®ä¾‹         new Sortable(goalsContainer, {           handle: '.drag-handle',           animation: 150,           ghostClass: 'sortable-ghost',           chosenClass: 'sortable-chosen',           dragClass: 'sortable-drag',           filter: '.text-center', // æ’é™¤"æ·»åŠ æ–°ç›®æ ‡"æŒ‰é’®åŒºåŸŸ           onEnd: function (evt) {             // è·å–æ‰€æœ‰ç›®æ ‡å¡ç‰‡çš„æ–°æ’åº             const goalCards = Array.from(goalsContainer.querySelectorAll('.goal-card'));             const newOrder = goalCards.map(card => card.dataset.goalId);              // æ›´æ–°æ•°æ®å­˜å‚¨             updateGoalOrder(taskId, newOrder);           }         });       }        // æ›´æ–°ç›®æ ‡æ’åº       function updateGoalOrder(taskId, newOrder) {         try {           // ä¼˜å…ˆä½¿ç”¨ DataStore API           if (window.TaskPixel && TaskPixel.DataStore && typeof TaskPixel.DataStore.updateGoalOrder === 'function') {             TaskPixel.DataStore.updateGoalOrder(taskId, newOrder);             return;           }         } catch (e) {           console.error('æ›´æ–°ç›®æ ‡æ’åºå¤±è´¥ via DataStore', e);         }          // å¤‡ç”¨æ–¹æ¡ˆï¼šç›´æ¥æ“ä½œlocalStorage         try {           const raw = localStorage.getItem('taskpixel_data');           if (!raw) return;            const data = JSON.parse(raw);           const task = data.tasks.find(t => t.id === taskId);           if (!task || !task.goals) return;            // é‡æ–°æ’åºç›®æ ‡           const reorderedGoals = [];           newOrder.forEach((goalId, index) => {             const goal = task.goals.find(g => g.id === goalId);             if (goal) {               goal.order = index;               reorderedGoals.push(goal);             }           });            task.goals = reorderedGoals;           localStorage.setItem('taskpixel_data', JSON.stringify(data));            console.log('ç›®æ ‡æ’åºå·²æ›´æ–°');         } catch (e) {           console.error('æ›´æ–°ç›®æ ‡æ’åºå¤±è´¥', e);         }       }        // åˆ›å»ºç›®æ ‡å¡ç‰‡       function createGoalCard(goal, taskId) {         const card = document.createElement('div');         card.className = 'border-4 border-black bg-white mb-6 p-4 goal-card';         card.dataset.goalId = goal.id;          // è®¡ç®—è¿›åº¦         const total = goal.substeps ? goal.substeps.length : 0;         const completed = goal.substeps ? goal.substeps.filter(s => s.completed).length : 0;          card.innerHTML = `           <div class="flex items-start justify-between mb-4">               <div class="drag-handle cursor-move text-gray-400 hover:text-gray-600 mr-3 mt-1" title="æ‹–æ‹½æ’åºç›®æ ‡">                 <svg width="14" height="14" viewBox="0 0 12 12" fill="currentColor">                   <circle cx="3" cy="3" r="1"/>                   <circle cx="9" cy="3" r="1"/>                   <circle cx="3" cy="6" r="1"/>                   <circle cx="9" cy="6" r="1"/>                   <circle cx="3" cy="9" r="1"/>                   <circle cx="9" cy="9" r="1"/>                 </svg>               </div>               <div class="flex-1">               <div class="flex items-center justify-between">                 <h4 class="font-display text-lg font-bold mb-2">${goal.title || 'æœªå‘½åç›®æ ‡'}</h4>                 <button class="toggle-substeps-btn pixel-tog