atch (e) {           alert('ä¿®æ”¹ç›®æ ‡å¤±è´¥: ' + e.message);         }       }        // åˆ é™¤ç›®æ ‡       function deleteGoal(taskId, goalId) {         try {           const raw = localStorage.getItem('taskpixel_data');           if (raw) {             const data = JSON.parse(raw);             const task = data.tasks.find(t => t.id === taskId);             if (task && task.goals) {               task.goals = task.goals.filter(g => g.id !== goalId);               localStorage.setItem('taskpixel_data', JSON.stringify(data));                // é‡æ–°æ¸²æŸ“               renderGoals(task.goals, taskId);               updateProgressBar(taskId);             }           }         } catch (e) {           alert('åˆ é™¤ç›®æ ‡å¤±è´¥: ' + e.message);         }       }        // æ‰“å¼€æ·»åŠ å­æ­¥éª¤å¯¹è¯æ¡†       function openAddSubstepDialog(goalId, taskId) {         const dialog = document.createElement('div');         dialog.className = 'fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50';         dialog.id = 'add-substep-dialog';          dialog.innerHTML = `           <div class="pixel-border bg-white p-6 w-full max-w-lg">             <h2 class="text-2xl font-display mb-6">æ·»åŠ å­æ­¥éª¤</h2>             <form id="add-substep-form">               <div class="mb-4">                 <label class="block font-display text-lg mb-2" for="substep-content">å­æ­¥éª¤å†…å®¹ *</label>                 <input type="text" id="substep-content" class="w-full" required placeholder="è¾“å…¥å­æ­¥éª¤å†…å®¹">               </div>               <div class="flex justify-end gap-4">                 <button type="button" id="cancel-add-substep" class="pixel-button">å–æ¶ˆ</button>                 <button type="submit" class="pixel-button bg-primary text-white">æ·»åŠ </button>               </div>             </form>           </div>         `;          document.body.appendChild(dialog);          // ç»‘å®šäº‹ä»¶         document.getElementById('add-substep-form').addEventListener('submit', function (e) {           e.preventDefault();           const content = document.getElementById('substep-content').value.trim();            if (!content) {             alert('è¯·è¾“å…¥å­æ­¥éª¤å†…å®¹');             return;           }            addSubstep(goalId, taskId, content);           dialog.remove();         });          document.getElementById('cancel-add-substep').addEventListener('click', () => {           dialog.remove();         });          // èšç„¦åˆ°å†…å®¹è¾“å…¥æ¡†         setTimeout(() => {           document.getElementById('substep-content').focus();         }, 100);       }        // æ·»åŠ å­æ­¥éª¤       function addSubstep(goalId, taskId, content) {         try {           const raw = localStorage.getItem('taskpixel_data');           if (raw) {             const data = JSON.parse(raw);             const task = data.tasks.find(t => t.id === taskId);             if (task && task.goals) {               const goal = task.goals.find(g => g.id === goalId);               if (goal) {                 if (!goal.substeps) goal.substeps = [];                  const newSubstep = {                   id: 'substep-' + Date.now(),                   content: content,                   completed: false                 };                  goal.substeps.push(newSubstep);                 localStorage.setItem('taskpixel_data', JSON.stringify(data));                  // é‡æ–°æ¸²æŸ“                 renderGoals(task.goals, taskId);                 updateProgressBar(taskId);               }             }           }         } catch (e) {           alert('æ·»åŠ å­æ­¥éª¤å¤±è´¥: ' + e.message);         }       }        // æ‰“å¼€ä¿®æ”¹å­æ­¥éª¤å¯¹è¯æ¡†       function openEditSubstepDialog(taskId, goalId, substepId) {         try {           const raw = localStorage.getItem('taskpixel_data');           if (!raw) return;            const data = JSON.parse(raw);           const task = data.tasks.find(t => t.id === taskId);           if (!task || !task.goals) return;            const goal = task.goals.find(g => g.id === goalId);           if (!goal || !goal.substeps) return;            const substep = goal.substeps.find(s => s.id === substepId);           if (!substep) return;            const dialog = document.createElement('div');           dialog.className = 'fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50';           dialog.id = 'edit-substep-dialog';            dialog.innerHTML = `             <div class="pixel-border bg-white p-6 w-full max-w-lg">               <h2 class="text-2xl font-display mb-6">ä¿®æ”¹å­æ­¥éª¤</h2>               <form id="edit-substep-form">                 <div class="mb-4">                   <label class="block font-display text-lg mb-2" for="edit-substep-content">å­æ­¥éª¤å†…å®¹ *</label>                   <input type="text" id="edit-substep-content" class="w-full" required placeholder="è¾“å…¥å­æ­¥éª¤å†…å®¹" value="${substep.content || substep.title || ''}">                 </div>                 <div class="mb-4">                   <label class="flex items-center">                     <input type="checkbox" id="edit-substep-completed" ${substep.completed ? 'checked' : ''} class="mr-2">                     <span class="font-display text-lg">å·²å®Œæˆ</span>                   </label>                 </div>                 <div class="flex justify-end gap-4">                   <button type="button" id="cancel-edit-substep" class="pixel-button">å–æ¶ˆ</button>                   <button type="submit" class="pixel-button bg-primary text-white">ä¿å­˜</button>                 </div>               </form>             </div>           `;            document.body.appendChild(dialog);            // ç»‘å®šäº‹ä»¶           document.getElementById('edit-substep-form').addEventListener('submit', function (e) {             e.preventDefault();             const content = document.getElementById('edit-substep-content').value.trim();             const completed = document.getElementById('edit-substep-completed').checked;              if (!content) {               alert('è¯·è¾“å…¥å­æ­¥éª¤å†…å®¹');               return;             }              // æ›´æ–°å­æ­¥éª¤             substep.content = content;             substep.completed = completed;             localStorage.setItem('taskpixel_data', JSON.stringify(data));              // é‡æ–°æ¸²æŸ“             renderGoals(task.goals, taskId);             updateProgressBar(taskId);             dialog.remove();           });            document.getElementById('cancel-edit-substep').addEventListener('click', () => {             dialog.remove();           });            // èšç„¦åˆ°å†…å®¹è¾“å…¥æ¡†           setTimeout(() => {             document.getElementById('edit-substep-content').focus();           }, 100);          } catch (e) {           alert('ä¿®æ”¹å­æ­¥éª¤å¤±è´¥: ' + e.message);         }       }        // åˆ é™¤å­æ­¥éª¤       function deleteSubstep(taskId, goalId, substepId) {         try {           const raw = localStorage.getItem('taskpixel_data');           if (raw) {             const data = JSON.parse(raw);             const task = data.tasks.find(t => t.id === taskId);             if (task && task.goals) {               const goal = task.goals.find(g => g.id === goalId);               if (goal && goal.substeps) {                 goal.substeps = goal.substeps.filter(s => s.id !== substepId);                 localStorage.setItem('taskpixel_data', JSON.stringify(data));                  // é‡æ–°æ¸²æŸ“                 renderGoals(task.goals, taskId);                 updateProgressBar(taskId);               }             }           }         } catch (e) {           alert('åˆ é™¤å­æ­¥éª¤å¤±è´¥: ' + e.message);         }       }        // æ›´æ–°å­æ­¥éª¤çŠ¶æ€       function updateSubstepStatus(taskId, goalId, substepId, completed) {         try {           const raw = localStorage.getItem('taskpixel_data');           if (raw) {             const data = JSON.parse(raw);             const task = data.tasks.find(t => t.id === taskId);             if (task && task.goals) {               const goal = task.goals.find(g => g.id === goalId);               if (goal && goal.substeps) {                 const substep = goal.substeps.find(s => s.id === substepId);                 if (substep) {                   substep.completed = completed;                   localStorage.setItem('taskpixel_data', JSON.stringify(data));                    // é‡æ–°æ¸²æŸ“ä»¥æ›´æ–°è¿›åº¦                   renderGoals(task.goals, taskId);                   updateProgressBar(taskId);                 }               }             }           }         } catch (e) {           alert('æ›´æ–°å­æ­¥éª¤çŠ¶æ€å¤±è´¥: ' + e.message);         }       }        // å†…è”æ ‡ç­¾ç®¡ç†å™¨       const InlineTagManager = {         activeInput: null,          init: function () {           this.bindEvents();           console.log('å†…è”æ ‡ç­¾ç®¡ç†å™¨åˆå§‹åŒ–å®Œæˆ');         },          bindEvents: function () {           // +Tags æŒ‰é’®ç‚¹å‡»äº‹ä»¶           document.addEventListener('click', (e) => {             if (e.target.classList.contains('add-tags-btn')) {               this.showTagInput(e.target);             }           });            // è¾“å…¥æ¡†è¾“å…¥äº‹ä»¶           document.addEventListener('input', (e) => {             if (e.target.classList.contains('tag-input-field')) {               this.handleTagInput(e.target);             }           });            // é”®ç›˜äº‹ä»¶           document.addEventListener('keydown', (e) => {             if (e.target.classList.contains('tag-input-field')) {               this.handleKeydown(e);             }           });            // ç‚¹å‡»å…¶ä»–åœ°æ–¹å…³é—­è¾“å…¥æ¡†           document.addEventListener('click', (e) => {             if (!e.target.closest('.tags-input-container')) {               this.hideAllTagInputs();             }           });         },          // æ˜¾ç¤ºæ ‡ç­¾è¾“å…¥æ¡†         showTagInput: function (button) {           this.hideAllTagInputs();            const container = button.closest('.tags-input-container');           const wrapper = container.querySelector('.tag-input-wrapper');           const input = wrapper.querySelector('.tag-input-field');            button.classList.add('hidden');           wrapper.classList.remove('hidden');            setTimeout(() => {             input.focus();             this.activeInput = input;           }, 50);         },          // éšè—æ‰€æœ‰æ ‡ç­¾è¾“å…¥æ¡†         hideAllTagInputs: function () {           document.querySelectorAll('.tags-input-container').forEach(container => {             const button = container.querySelector('.add-tags-btn');             const wrapper = container.querySelector('.tag-input-wrapper');             const input = wrapper.querySelector('.tag-input-field');             const suggestions = wrapper.querySelector('.tag-suggestions');              button.classList.remove('hidden');             wrapper.classList.add('hidden');             suggestions.classList.add('hidden');             input.value = '';           });            this.activeInput = null;         },          // å¤„ç†æ ‡ç­¾è¾“å…¥         handleTagInput: function (input) {           const query = input.value.trim();           if (query.length === 0) {             this.hideSuggestions(input);             return;           }            // è·å–ç°æœ‰æ ‡ç­¾è¿›è¡Œæœç´¢           const existingTags = this.getAllTags();           const filteredTags = existingTags.filter(tag =>             tag.name.toLowerCase().includes(query.toLowerCase())           );            this.showSuggestions(input, filteredTags, query);         },          // å¤„ç†é”®ç›˜äº‹ä»¶         handleKeydown: function (e) {           const input = e.target;           const suggestions = input.parentElement.querySelector('.tag-suggestions');            if (e.key === 'Escape') {             this.hideAllTagInputs();             return;           }            if (e.key === 'Enter') {             e.preventDefault();             const selectedItem = suggestions.querySelector('.tag-suggestion-item.selected');              if (selectedItem) {               if (selectedItem.dataset.tagId === 'create-new') {                 this.createAndAddTag(input, selectedItem.dataset.tagName);               } else {                 this.selectTag(input, selectedItem.dataset.tagId);               }             } else {               const query = input.value.trim();               if (query) {                 this.createAndAddTag(input, query);               }             }             return;           }            if (e.key === 'ArrowDown' || e.key === 'ArrowUp') {             e.preventDefault();             this.navigateSuggestions(suggestions, e.key === 'ArrowDown');             return;           }         },          // æ˜¾ç¤ºå»ºè®®         showSuggestions: function (input, tags, query) {           const suggestions = input.parentElement.querySelector('.tag-suggestions');           suggestions.innerHTML = '';            // æ·»åŠ ç°æœ‰æ ‡ç­¾å»ºè®®           tags.forEach(tag => {             const item = document.createElement('div');             item.className = 'tag-suggestion-item';             item.dataset.tagId = tag.id;             // ä½¿ç”¨ fallback æœºåˆ¶ç¡®ä¿æ ‡ç­¾æ˜¾ç¤º             const displayText = tag.display_text || '#' + tag.name || '#' + tag.id;             item.innerHTML = `<span style="color: ${tag.color || '#374151'}">${displayText}</span>`;             item.addEventListener('click', () => this.selectTag(input, tag.id));             suggestions.appendChild(item);           });            // æ£€æŸ¥æ˜¯å¦å·²å­˜åœ¨å®Œå…¨åŒ¹é…çš„æ ‡ç­¾           const existingTags = this.getAllTags();           const exactMatch = existingTags.find(tag =>             tag.name.toLowerCase() === query.toLowerCase()           );            // å¦‚æœæ²¡æœ‰åŒ¹é…çš„æ ‡ç­¾ä¸”æ²¡æœ‰å®Œå…¨åŒ¹é…çš„æ ‡ç­¾ï¼Œæ˜¾ç¤ºåˆ›å»ºæ–°æ ‡ç­¾é€‰é¡¹           if (tags.length === 0 && query && !exactMatch) {             const createItem = document.createElement('div');             createItem.className = 'tag-suggestion-item';             createItem.dataset.tagId = 'create-new';             createItem.dataset.tagName = query;             createItem.innerHTML = `åˆ›å»ºæ–°æ ‡ç­¾: <strong>#${query}</strong>`;             createItem.addEventListener('click', () => this.createAndAddTag(input, query));             suggestions.appendChild(createItem);           } else if (exactMatch && tags.length === 0) {             // å¦‚æœæœ‰å®Œå…¨åŒ¹é…ä½†ä¸åœ¨è¿‡æ»¤ç»“æœä¸­ï¼Œæ˜¾ç¤ºè¯¥æ ‡ç­¾             const item = document.createElement('div');             item.className = 'tag-suggestion-item';             item.dataset.tagId = exactMatch.id;             item.innerHTML = `<span style="color: ${exactMatch.color || '#374151'}">#${exactMatch.name}</span>`;             item.addEventListener('click', () => this.selectTag(input, exactMatch.id));             suggestions.appendChild(item);           }            suggestions.classList.remove('hidden');         },          // éšè—å»ºè®®         hideSuggestions: function (input) {           const suggestions = input.parentElement.querySelector('.tag-suggestions');           suggestions.classList.add('hidden');         },          // å¯¼èˆªå»ºè®®åˆ—è¡¨         navigateSuggestions: function (suggestions, isDown) {           const items = suggestions.querySelectorAll('.tag-suggestion-item');           if (items.length === 0) return;            const currentSelected = suggestions.querySelector('.tag-suggestion-item.selected');           let newIndex = 0;            if (currentSelected) {             currentSelected.classList.remove('selected');             const currentIndex = Array.from(items).indexOf(currentSelected);              if (isDown) {               newIndex = (currentIndex + 1) % items.length;             } else {               newIndex = currentIndex === 0 ? items.length - 1 : currentIndex - 1;             }           }            items[newIndex].classList.add('selected');         },          // é€‰æ‹©æ ‡ç­¾         selectTag: function (input, tagId) {           const container = input.closest('.tags-input-container');           const target = container.dataset.target;           const taskId = container.dataset.taskId;           const goalId = container.dataset.goalId;            if (target === 'task') {             this.addTagToTask(taskId, tagId);           } else if (target === 'goal') {             this.addTagToGoal(taskId, goalId, tagId);           }            this.hideAllTagInputs();         },          // åˆ›å»ºå¹¶æ·»åŠ æ–°æ ‡ç­¾         createAndAddTag: function (input, tagName) {           // ä½¿ç”¨TagManageråˆ›å»ºæ–°æ ‡ç­¾           if (window.TaskPixel && window.TaskPixel.TagManager) {             const result = window.TaskPixel.TagManager.createTag({ name: tagName });             if (result.success) {               this.selectTag(input, result.tagId);             } else if (result.reason === 'tag_exists') {               this.selectTag(input, result.tagId);             } else {               alert('åˆ›å»ºæ ‡ç­¾å¤±è´¥ï¼Œè¯·é‡è¯•');             }           } else {             // å¤‡ç”¨æ–¹æ¡ˆï¼šç›´æ¥åˆ›å»ºç®€å•æ ‡ç­¾             const cleanTagName = tagName.replace(/^#/, "").trim();              // æ£€æŸ¥æ ‡ç­¾æ˜¯å¦å·²å­˜åœ¨             const data = JSON.parse(localStorage.getItem('taskpixel_data') || '{}');             if (!data.tags) data.tags = [];              const existingTag = data.tags.find(tag => tag.name.toLowerCase() === cleanTagName.toLowerCase());             if (existingTag) {               // å¦‚æœæ ‡ç­¾å·²å­˜åœ¨ï¼Œç›´æ¥é€‰æ‹©ç°æœ‰æ ‡ç­¾               this.selectTag(input, existingTag.id);               return;             }              const newTag = {               id: 'tag-' + Date.now(),               name: cleanTagName,               display_text: '#' + cleanTagName,               color: this.getRandomTagColor(),               created_at: new Date().toISOString(),               usage_count: 0             };              data.tags.push(newTag);             localStorage.setItem('taskpixel_data', JSON.stringify(data));              // é€šçŸ¥ DataStore é‡æ–°åŠ è½½æ•°æ®             if (typeof TaskPixel !== 'undefined' && TaskPixel.DataStore) {               TaskPixel.DataStore.loadFromStorage();             }              // ä½¿ TagManager ç¼“å­˜å¤±æ•ˆ             if (typeof TaskPixel !== 'undefined' && TaskPixel.TagManager) {               TaskPixel.TagManager.invalidateCache();             }              this.selectTag(input, newTag.id);           }         },          // è·å–æ‰€æœ‰æ ‡ç­¾         getAllTags: function () {           try {             const data = JSON.parse(localStorage.getItem('taskpixel_data') || '{}');             return data.tags || [];           } catch (e) {             return [];           }         },          // è·å–éšæœºæ ‡ç­¾é¢œè‰²         getRandomTagColor: function () {           const colors = ['#374151', '#6B7280', '#4B5563', '#1F2937', '#065F46', '#7C2D12', '#581C87', '#92400E'];           return colors[Math.floor(Math.random() * colors.length)];         },          // æ·»åŠ æ ‡ç­¾åˆ°ä»»åŠ¡         addTagToTask: function (taskId, tagId) {           try {             const data = JSON.parse(localStorage.getItem('taskpixel_data') || '{}');             const task = data.tasks?.find(t => t.id === taskId);              if (task) {               if (!task.tags) task.tags = [];               if (!task.tags.includes(tagId)) {                 task.tags.push(tagId);                 localStorage.setItem('taskpixel_data', JSON.stringify(data));                  // é€šçŸ¥ DataStore é‡æ–°åŠ è½½æ•°æ®                 if (typeof TaskPixel !== 'undefined' && TaskPixel.DataStore) {                   TaskPixel.DataStore.loadFromStorage();                 }                  this.renderTaskTags(taskId);               }             }           } catch (e) {             console.error('æ·»åŠ ä»»åŠ¡æ ‡ç­¾å¤±è´¥:', e);           }         },          // æ·»åŠ æ ‡ç­¾åˆ°ç›®æ ‡         addTagToGoal: function (taskId, goalId, tagId) {           try {             const data = JSON.parse(localStorage.getItem('taskpixel_data') || '{}');             const task = data.tasks?.find(t => t.id === taskId);             const goal = task?.goals?.find(g => g.id === goalId);              if (goal) {               if (!goal.tags) goal.tags = [];               if (!goal.tags.includes(tagId)) {                 goal.tags.push(tagId);                 localStorage.setItem('taskpixel_data', JSON.stringify(data));                  // é€šçŸ¥ DataStore é‡æ–°åŠ è½½æ•°æ®                 if (typeof TaskPixel !== 'undefined' && TaskPixel.DataStore) {                   TaskPixel.DataStore.loadFromStorage();                 }                  this.renderGoalTags(goalId);               }             }           } catch (e) {             console.error('æ·»åŠ ç›®æ ‡æ ‡ç­¾å¤±è´¥:', e);           }         },          // æ¸²æŸ“ä»»åŠ¡æ ‡ç­¾         renderTaskTags: function (taskId) {           try {             const data = JSON.parse(localStorage.getItem('taskpixel_data') || '{}');             const task = data.tasks?.find(t => t.id === taskId);             const allTags = data.tags || [];              const container = document.querySelector('.task-tags-display');             if (!container || !task) return;              container.innerHTML = '';              if (task.tags && task.tags.length > 0) {               task.tags.forEach(tagId => {                 const tag = allTags.find(t => t.id === tagId);                 if (tag) {                   const tagElement = this.createTagElement(tag, () => this.removeTagFromTask(taskId, tagId));                   container.appendChild(tagElement);                 }               });             }           } catch (e) {             console.error('æ¸²æŸ“ä»»åŠ¡æ ‡ç­¾å¤±è´¥:', e);           }         },          // æ¸²æŸ“ç›®æ ‡æ ‡ç­¾         renderGoalTags: function (goalId) {           try {             const data = JSON.parse(localStorage.getItem('taskpixel_data') || '{}');             const allTags = data.tags || [];              // æ‰¾åˆ°ç›®æ ‡             let goal = null;             for (const task of data.tasks || []) {               const foundGoal = task.goals?.find(g => g.id === goalId);               if (foundGoal) {                 goal = foundGoal;                 break;               }             }              const container = document.querySelector(`[data-goal-id="${goalId}"] .goal-tags-display`);             if (!container || !goal) return;              container.innerHTML = '';              if (goal.tags && goal.tags.length > 0) {               goal.tags.forEach(tagId => {                 const tag = allTags.find(t => t.id === tagId);                 if (tag) {                   const tagElement = this.createTagElement(tag, () => this.removeTagFromGoal(goalId, tagId));                   container.appendChild(tagElement);                 }               });             }           } catch (e) {             console.error('æ¸²æŸ“ç›®æ ‡æ ‡ç­¾å¤±è´¥:', e);           }         },          // åˆ›å»ºæ ‡ç­¾å…ƒç´          createTagElement: function (tag, onRemove) {           const tagElement = document.createElement('span');           tagElement.className = 'task-tag';           tagElement.style.color = tag.color || '#374151';           // ä½¿ç”¨ fallback æœºåˆ¶ç¡®ä¿æ ‡ç­¾æ˜¾ç¤º           const displayText = tag.display_text || '#' + tag.name || '#' + tag.id;           tagElement.textContent = displayText;           tagElement.title = 'ç‚¹å‡»ä»ä»»åŠ¡ä¸­ç§»é™¤æ­¤æ ‡ç­¾';            tagElement.addEventListener('click', (e) => {             e.preventDefault();             if (confirm(`ç¡®å®šè¦ä»æ­¤ä»»åŠ¡ä¸­ç§»é™¤æ ‡ç­¾ ${displayText} å—ï¼Ÿ\n\næ³¨æ„ï¼šè¿™åªä¼šä»å½“å‰ä»»åŠ¡ä¸­ç§»é™¤æ ‡ç­¾ï¼Œä¸ä¼šåˆ é™¤æ ‡ç­¾æœ¬èº«ã€‚`)) {               onRemove();             }           });            return tagElement;         },          // ä»ä»»åŠ¡ç§»é™¤æ ‡ç­¾         removeTagFromTask: function (taskId, tagId) {           try {             // ä½¿ç”¨ DataStore API è€Œä¸æ˜¯ç›´æ¥æ“ä½œ localStorage             const task = TaskPixel.DataStore.getTaskById(taskId);              if (task && task.tags) {               task.tags = task.tags.filter(id => id !== tagId);               // ä½¿ç”¨ DataStore çš„ä¿å­˜æ–¹æ³•ï¼Œç¡®ä¿æ•°æ®åŒæ­¥               TaskPixel.DataStore.saveToStorage();               this.renderTaskTags(taskId);                // è§¦å‘äº‹ä»¶é€šçŸ¥å…¶ä»–ç»„ä»¶               TaskPixel.EventBus.emit("task:tags_updated", {                 taskId: taskId,                 tags: task.tags,               });             }           } catch (e) {             console.error('ç§»é™¤ä»»åŠ¡æ ‡ç­¾å¤±è´¥:', e);           }         },          // ä»ç›®æ ‡ç§»é™¤æ ‡ç­¾         removeTagFromGoal: function (goalId, tagId) {           try {             // ä½¿ç”¨ DataStore API è€Œä¸æ˜¯ç›´æ¥æ“ä½œ localStorage             const tasks = TaskPixel.DataStore.getAllTasks();              // æ‰¾åˆ°å¹¶æ›´æ–°ç›®æ ‡             for (const task of tasks) {               const goal = task.goals?.find(g => g.id === goalId);               if (goal && goal.tags) {                 goal.tags = goal.tags.filter(id => id !== tagId);                 // ä½¿ç”¨ DataStore çš„ä¿å­˜æ–¹æ³•ï¼Œç¡®ä¿æ•°æ®åŒæ­¥                 TaskPixel.DataStore.saveToStorage();                 this.renderGoalTags(goalId);                  // è§¦å‘äº‹ä»¶é€šçŸ¥å…¶ä»–ç»„ä»¶                 TaskPixel.EventBus.emit("goal:tags_updated", {                   goalId: goalId,                   tags: goal.tags,                 });                 break;               }             }           } catch (e) {             console.error('ç§»é™¤ç›®æ ‡æ ‡ç­¾å¤±è´¥:', e);           }         }       };        // å†…è”æ ‡ç­¾ç®¡ç†å™¨åˆå§‹åŒ–å°†åœ¨å®šä¹‰åè¿›è¡Œ        // æ›´æ–°è¿›åº¦æ¡       function updateProgressBar(taskId) {         if (!taskId) {           console.warn('updateProgressBar: taskId is missing');           return;         }          setTimeout(() => {           try {             // è·å–å®é™…è¿›åº¦             let actualProgress = 0;              if (window.TaskPixel?.DataStoreAdapter?.getTaskProgress) {               actualProgress = window.TaskPixel.DataStoreAdapter.getTaskProgress(taskId);             } else {               // å¤‡ç”¨è®¡ç®—æ–¹æ³•               const raw = localStorage.getItem('taskpixel_data');               if (raw) {                 const data = JSON.parse(raw);                 const task = data.tasks?.find(t => t.id === taskId);                 if (task) {                   // è®¡ç®—åŸºäºç›®æ ‡å’Œå­æ­¥éª¤çš„è¿›åº¦                   let totalSubsteps = 0;                   let completedSubsteps = 0;                    if (task.goals && Array.isArray(task.goals)) {                     task.goals.forEach(goal => {                       if (goal.substeps && Array.isArray(goal.substeps)) {                         goal.substeps.forEach(substep => {                           totalSubsteps++;                           if (substep.completed) {                             completedSubsteps++;                           }                         });                       }                     });                   }                    actualProgress = totalSubsteps > 0 ? Math.round((completedSubsteps / totalSubsteps) * 100) : 0;                 }               }             }              // æ›´æ–°è¿›åº¦æ¡             const progressEl = document.querySelector('.progress-bar-fill');             const progressTextEl = document.querySelector('.progress-text');              if (progressEl) {               progressEl.style.width = actualProgress + '%';             }             if (progressTextEl) {               progressTextEl.textContent = actualProgress + '% å®Œæˆ';             }             // æ›´æ–°å†…éƒ¨æ ‡ç­¾ï¼ˆè‹¥å­˜åœ¨ï¼‰             const progressLabelEl = document.querySelector('.progress-label');             if (progressLabelEl) {               progressLabelEl.textContent = actualProgress + '%';               if (actualProgress < 10) {                 progressLabelEl.classList.add('hidden-small');               } else {                 progressLabelEl.classList.remove('hidden-small');               }             }            } catch (e) {             console.error('Error updating progress bar:', e);           }         }, 100);       }        // åˆå§‹åŒ–å†…è”æ ‡ç­¾ç®¡ç†å™¨       InlineTagManager.init();        // åˆå§‹åŒ–æ ‡ç­¾é¡µåŠŸèƒ½       initTabSystem();        // åˆå§‹åŒ–èµ„æºç®¡ç†åŠŸèƒ½       initResourceManagement();      });      // æ ‡ç­¾é¡µç³»ç»Ÿ     function initTabSystem() {       const tabButtons = document.querySelectorAll('.tab-btn');       const tabContents = document.querySelectorAll('.tab-content');        tabButtons.forEach(button => {         button.addEventListener('click', function () {           const targetTab = this.getAttribute('data-tab');            // ç§»é™¤æ‰€æœ‰æ´»è·ƒçŠ¶æ€           tabButtons.forEach(btn => btn.classList.remove('active'));           tabContents.forEach(content => content.classList.add('hidden'));            // æ¿€æ´»å½“å‰æ ‡ç­¾é¡µ           this.classList.add('active');           const targetContent = document.querySelector(`.tab-content[data-tab="${targetTab}"]`);           if (targetContent) {             targetContent.classList.remove('hidden');           }            // å¦‚æœåˆ‡æ¢åˆ°èµ„æºæ ‡ç­¾é¡µï¼Œåˆ·æ–°èµ„æºåˆ—è¡¨           if (targetTab === 'resources') {             refreshResourceList();           }         });       });     }      // èµ„æºç®¡ç†ç³»ç»Ÿ     function initResourceManagement() {       const addButtons = document.querySelectorAll('.resource-add-btn');        addButtons.forEach(button => {         button.addEventListener('click', function () {           const uploadMethod = this.getAttribute('data-method');           showAddResourceDialog(uploadMethod);         });       });        // åˆå§‹åŒ–æ—¶åˆ·æ–°èµ„æºåˆ—è¡¨       refreshResourceList();     }      // æ˜¾ç¤ºæ·»åŠ èµ„æºå¯¹è¯æ¡†     function showAddResourceDialog(method) {       if (method === 'local') {         // æœ¬åœ°æ–‡ä»¶ä¸Šä¼          showLocalFileDialog();       } else if (method === 'url') {         // URLé“¾æ¥ä¸Šä¼          showUrlDialog();       }     }      // æœ¬åœ°æ–‡ä»¶ä¸Šä¼ å¯¹è¯æ¡†     function showLocalFileDialog() {       // åˆ›å»ºæ–‡ä»¶è¾“å…¥å…ƒç´        const fileInput = document.createElement('input');       fileInput.type = 'file';       fileInput.style.display = 'none';       fileInput.accept = '*/*'; // æ¥å—æ‰€æœ‰ç±»å‹çš„æ–‡ä»¶        fileInput.addEventListener('change', function (e) {         const file = e.target.files[0];         if (file) {           const name = prompt('è¯·è¾“å…¥èµ„æºåç§°:', file.name);           if (name) {             // æ ¹æ®æ–‡ä»¶ç±»å‹è‡ªåŠ¨åˆ¤æ–­èµ„æºç±»å‹             const resourceType = getResourceTypeFromFile(file);             // æœ¬åœ°æ–‡ä»¶ï¼Œè®°å½•æ–‡ä»¶è·¯å¾„ï¼ˆå®é™…é¡¹ç›®ä¸­å¯èƒ½éœ€è¦ä¸Šä¼ åˆ°æœåŠ¡å™¨ï¼‰             const filePath = file.name; // ç®€åŒ–å¤„ç†ï¼Œå®é™…åº”è¯¥æ˜¯å®Œæ•´è·¯å¾„             addResource(resourceType, name, filePath, false, file);           }         }         document.body.removeChild(fileInput);       });        document.body.appendChild(fileInput);       fileInput.click();     }      // URLé“¾æ¥ä¸Šä¼ å¯¹è¯æ¡†     function showUrlDialog() {       const name = prompt('è¯·è¾“å…¥èµ„æºåç§°:');       if (!name) return;        const url = prompt('è¯·è¾“å…¥èµ„æºçš„URLé“¾æ¥:\n\nç¤ºä¾‹:\nâ€¢ å›¾ç‰‡: https://example.com/image.jpg\nâ€¢ æ–‡ä»¶: https://example.com/file.pdf\nâ€¢ ç½‘ç«™: https://example.com');       if (!url) return;        // ç®€å•çš„URLéªŒè¯       if (!isValidUrl(url)) {         alert('è¯·è¾“å…¥æœ‰æ•ˆçš„URLåœ°å€ï¼');         return;       }        // æ ¹æ®URLè‡ªåŠ¨åˆ¤æ–­èµ„æºç±»å‹       const resourceType = getResourceTypeFromUrl(url);       addResource(resourceType, name, url, true);     }      // URLéªŒè¯å‡½æ•°     function isValidUrl(string) {       try {         new URL(string);         return true;       } catch (_) {         return false;       }     }      // æ ¹æ®æ–‡ä»¶ç±»å‹åˆ¤æ–­èµ„æºç±»å‹     function getResourceTypeFromFile(file) {       const mimeType = file.type.toLowerCase();       const fileName = file.name.toLowerCase();        if (mimeType.startsWith('image/')) {         return 'image';       } else if (mimeType.includes('pdf') || fileName.endsWith('.pdf') ||         mimeType.includes('document') || fileName.match(/\.(doc|docx|txt|md|rtf)$/)) {         return 'document';       } else if (fileName.match(/\.(html|htm)$/)) {         return 'link';       } else {         return 'file';       }     }      // æ ¹æ®URLåˆ¤æ–­èµ„æºç±»å‹     function getResourceTypeFromUrl(url) {       const urlLower = url.toLowerCase();        if (urlLower.match(/\.(jpg|jpeg|png|gif|bmp|svg|webp)($|\?)/)) {         return 'image';       } else if (urlLower.match(/\.(pdf|doc|docx|txt|md|rtf)($|\?)/)) {         return 'document';       } else if (urlLower.match(/\.(html|htm)($|\?)/)) {         return 'link';       } else {         return 'file';       }     }      // æ·»åŠ èµ„æº     function addResource(type, name, path, isUrl = false, fileObj = null) {       try {         const taskId = new URLSearchParams(window.location.search).get('id');         if (!taskId) {           alert('æœªæ‰¾åˆ°ä»»åŠ¡ID');           return;         }          const data = JSON.parse(localStorage.getItem('taskpixel_data') || '{}');         const task = data.tasks?.find(t => t.id === taskId);          if (!task) {           alert('æœªæ‰¾åˆ°ä»»åŠ¡');           return;         }          // åˆå§‹åŒ–èµ„æºæ•°ç»„         if (!task.resources) {           task.resources = [];         }          // æ·»åŠ æ–°èµ„æº         const resource = {           id: 'resource_' + Date.now(),           type: type,           name: name,           path: path,           isUrl: isUrl,           addDate: new Date().toISOString(),           size: fileObj ? fileObj.size : null,           fileType: fileObj ? fileObj.type : null         };          task.resources.push(resource);          // ä¿å­˜æ•°æ®         localStorage.setItem('taskpixel_data', JSON.stringify(data));          // åˆ·æ–°æ˜¾ç¤º         refreshResourceList();          const methodText = isUrl ? 'URLé“¾æ¥' : 'æœ¬åœ°æ–‡ä»¶';         alert(`${methodText}æ·»åŠ æˆåŠŸï¼`);        } catch (e) {         console.error('æ·»åŠ èµ„æºå¤±è´¥:', e);         alert('æ·»åŠ èµ„æºå¤±è´¥: ' + e.message);       }     }      // åˆ é™¤èµ„æº     function deleteResource(resourceId) {       if (!confirm('ç¡®å®šè¦åˆ é™¤è¿™ä¸ªèµ„æºå—ï¼Ÿ')) {         return;       }        try {         const taskId = new URLSearchParams(window.location.search).get('id');         if (!taskId) return;          const data = JSON.parse(localStorage.getItem('taskpixel_data') || '{}');         const task = data.tasks?.find(t => t.id === taskId);          if (!task || !task.resources) return;          // åˆ é™¤èµ„æº         task.resources = task.resources.filter(r => r.id !== resourceId);          // ä¿å­˜æ•°æ®         localStorage.setItem('taskpixel_data', JSON.stringify(data));          // åˆ·æ–°æ˜¾ç¤º         refreshResourceList();          alert('èµ„æºåˆ é™¤æˆåŠŸï¼');        } catch (e) {         console.error('åˆ é™¤èµ„æºå¤±è´¥:', e);         alert('åˆ é™¤èµ„æºå¤±è´¥: ' + e.message);       }     }      // åˆ·æ–°èµ„æºåˆ—è¡¨     function refreshResourceList() {       try {         const taskId = new URLSearchParams(window.location.search).get('id');         if (!taskId) return;          const data = JSON.parse(localStorage.getItem('taskpixel_data') || '{}');         const task = data.tasks?.find(t => t.id === taskId);          const resourceList = document.getElementById('resourceList');         const emptyState = document.getElementById('emptyResourcesState');          if (!resourceList || !emptyState) return;          // æ¸…ç©ºåˆ—è¡¨         resourceList.innerHTML = '';          if (!task || !task.resources || task.resources.length === 0) {           // æ˜¾ç¤ºç©ºçŠ¶æ€           emptyState.style.display = 'block';           return;         }          // éšè—ç©ºçŠ¶æ€         emptyState.style.display = 'none';          // èµ„æºå›¾æ ‡æ˜ å°„         const resourceIcons = {           image: 'ğŸ“·',           file: 'ğŸ“',           document: 'ğŸ“„',           link: 'ğŸ”—'         };          // æ¸²æŸ“èµ„æºåˆ—è¡¨         task.resources.forEach(resource => {           const resourceItem = document.createElement('div');           resourceItem.className = 'resource-item';            // æ ¹æ®èµ„æºç±»å‹æ„å»ºä¸åŒçš„HTMLç»“æ„           const isClickable = resource.isUrl;           const sizeText = resource.size ? formatFileSize(resource.size) : '';           const typeText = resource.fileType || '';            resourceItem.innerHTML = `             <div class="resource-item-header">               <div class="resource-icon">${resourceIcons[resource.type] || 'ğŸ“'}</div>               <div class="resource-info flex-1" ${isClickable ? `style="cursor: pointer;" onclick="openResource('${resource.path}')"` : ''}>                 <div class="resource-name" style="${isClickable ? 'color: #3b82f6; text-decoration: underline;' : ''}">${resource.name}</div>                 <div class="resource-path">${resource.path}</div>                 <div class="text-xs text-gray-400 mt-1 flex gap-2">                   <span>${resource.isUrl ? 'ğŸ”— URLé“¾æ¥' : 'ğŸ’¾ æœ¬åœ°æ–‡ä»¶'}</span>                   <span>â€¢</span>                   <span>${new Date(resource.addDate).toLocaleDateString()}</span>                   ${sizeText ? `<span>â€¢ ${sizeText}</span>` : ''}                   ${typeText ? `<span>â€¢ ${typeText}</span>` : ''}                 </div>                 ${isClickable ? '<div class="text-xs text-blue-500 mt-1">ğŸ‘† ç‚¹å‡»è®¿é—®é“¾æ¥</div>' : ''}               </div>               <div class="flex gap-2">                 ${isClickable ? `<button class="pixel-button bg-blue-500 text-white text-xs px-2 py-1" onclick="openResource('${resource.path}')">è®¿é—®</button>` : ''}                 <button class="resource-delete pixel-button bg-red-500 text-white text-xs px-2 py-1" onclick="deleteResource('${resource.id}')">                   åˆ é™¤                 </button>               </div>             </div>           `;            resourceList.appendChild(resourceItem);         });        } catch (e) {         console.error('åˆ·æ–°èµ„æºåˆ—è¡¨å¤±è´¥:', e);       }     }      // æ–‡ä»¶å¤§å°æ ¼å¼åŒ–     function formatFileSize(bytes) {       if (bytes === 0) return '0 Bytes';       const k = 1024;       const sizes = ['Bytes', 'KB', 'MB', 'GB'];       const i = Math.floor(Math.log(bytes) / Math.log(k));       return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];     }      // æ‰“å¼€èµ„æºé“¾æ¥     function openResource(url) {       if (!url) return;        try {         // åœ¨æ–°æ ‡ç­¾é¡µä¸­æ‰“å¼€é“¾æ¥         window.open(url, '_blank', 'noopener,noreferrer');       } catch (e) {         console.error('æ‰“å¼€é“¾æ¥å¤±è´¥:', e);         alert('æ— æ³•æ‰“å¼€é“¾æ¥: ' + url);       }     }      // å°†å‡½æ•°è®¾ä¸ºå…¨å±€ï¼Œä»¥ä¾¿åœ¨onclickä¸­è°ƒç”¨     window.deleteResource = deleteResource;     window.openResource = openResource;    </script> </body>  </html>[]
